SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_agrupamento_enf (nr_prescricao_p bigint) AS $body$
DECLARE


ds_horarios_w		varchar(2000);
ds_horarios_ww		varchar(2000);
nr_agrupamento_w	double precision;

C01 CURSOR FOR
	SELECT	distinct
		nr_agrupamento
	from	prescr_material
	where	nr_prescricao =  nr_prescricao_p
	and	ie_agrupador in (1,5)
	and	coalesce(nr_sequencia_solucao::text, '') = ''
	and	coalesce(nr_sequencia_dieta::text, '') = ''
	and	coalesce(nr_sequencia_diluicao::text, '') = ''
	and     coalesce(dt_suspensao::text, '') = ''
	and     coalesce(ie_administrar,'S') = 'S'
	and	ie_agrupador in (1,5)
	and	obter_tipo_material(cd_material, 'C') in (2,3);

C02 CURSOR FOR
	SELECT	ds_horarios
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	nr_agrupamento = nr_agrupamento_w;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_agrupamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_horarios_ww := null;
		open C02;
		loop
		fetch C02 into
			ds_horarios_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			if (ds_horarios_ww IS NOT NULL AND ds_horarios_ww::text <> '') and (ds_horarios_ww <> ds_horarios_w)then

				--Não é permitido liberar a prescrição que tenha itens agrupados com horários diferentes!'||'#@#@');
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(261463);
			end if;
			ds_horarios_ww := ds_horarios_w;
			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;

end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_agrupamento_enf (nr_prescricao_p bigint) FROM PUBLIC;

