SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_anestesista_escala ( lista_informacao_p text, nr_seq_escala_dir_p bigint, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE

					 
qt_anestesista_w		integer;
ds_consistencia_w		varchar(255);
ds_consistencia_anestesita_w	varchar(255);
nr_seq_escala_dir_w		bigint;
cd_profissional_w		varchar(10);

lista_informacao_w		varchar(2000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;


BEGIN 
 
lista_informacao_w	:= substr(lista_informacao_p,1,2000);
 
while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or 
	ie_contador_w > 200 loop 
	begin 
	tam_lista_w		:= length(lista_informacao_w);
	ie_pos_virgula_w	:= position(',' in lista_informacao_w);
 
	if (ie_pos_virgula_w <> 0) then 
		cd_profissional_w	:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;
	 
	if (nr_seq_escala_dir_p > 0) then 
		 
		select	count(*) 
		into STRICT	qt_anestesista_w 
		from	escala_diaria_adic 
		where	cd_pessoa_fisica	=	cd_profissional_w 
		and	nr_seq_escala_diaria	=	nr_seq_escala_dir_p;
		 
		if (qt_anestesista_w = 0) then 
			 
			select	obter_se_prof_liberado_escala(cd_profissional_w,b.nr_seq_escala,b.dt_inicio) 
			into STRICT	ds_consistencia_anestesita_w 
			from	escala_diaria b 
			where	b.nr_sequencia 		= nr_seq_escala_dir_p;
		 
			if (ds_consistencia_anestesita_w IS NOT NULL AND ds_consistencia_anestesita_w::text <> '') and (length(ds_consistencia_w || ds_consistencia_anestesita_w || chr(10)) < 255) then 
			 
				ds_consistencia_w	:= replace(ds_consistencia_w,ds_consistencia_anestesita_w,'');
				ds_consistencia_w	:= ds_consistencia_w || ds_consistencia_anestesita_w || chr(10);		
 
			end if;
		end if;
	end if;
						 
	ie_contador_w	:= ie_contador_w + 1;
	end;
end loop;
 
if (ds_consistencia_w IS NOT NULL AND ds_consistencia_w::text <> '') then 
	ds_consistencia_p := Wheb_mensagem_pck.get_texto(306431, 'DS_CONSISTENCIA_W='||ds_consistencia_w); -- 'ConsistÃªncias: ' || chr(10) || ds_consistencia_w;	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
