CREATE OR REPLACE PROCEDURE consistir_bloqueio_agenda (cd_agenda_p bigint, dt_horario_p timestamp, ie_dia_semana_p bigint, ie_bloqueio_p INOUT text, ie_classif_agenda_p text default null) AS $body$
DECLARE


ie_bloqueio_w varchar(1) 	:= 'N';
qt_periodo_w	bigint	:= 0;
qt_horario_w	bigint	:= 0;
qt_dia_w	bigint	:= 0;
qt_dia_hor_w	bigint	:= 0;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') and (ie_dia_semana_p IS NOT NULL AND ie_dia_semana_p::text <> '') then
	/* consistir bloqueio periodo */

	/*SO-2220426*/

	begin
	select	count(*)
	into STRICT	qt_periodo_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(ie_classif_bloqueio, coalesce(ie_classif_agenda_p, 'XPTO')) = coalesce(ie_classif_agenda_p, 'XPTO')
	and	coalesce(hr_inicio_bloqueio::text, '') = ''
	and	coalesce(hr_final_bloqueio::text, '') = ''
	and	coalesce(ie_dia_semana::text, '') = '';
	exception
		when others then
		qt_periodo_w := 0;
	end;

	/* consistir bloqueio horario */

	/*SO-2220426*/

	begin
	select	count(*)
	into STRICT	qt_horario_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(ie_classif_bloqueio, coalesce(ie_classif_agenda_p, 'XPTO')) = coalesce(ie_classif_agenda_p, 'XPTO')
	and	dt_horario_p between to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	to_date(to_char(hr_inicio_bloqueio,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') < to_date(to_char(hr_final_bloqueio,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	(hr_inicio_bloqueio IS NOT NULL AND hr_inicio_bloqueio::text <> '')
	and	(hr_final_bloqueio IS NOT NULL AND hr_final_bloqueio::text <> '')
	and	coalesce(ie_dia_semana::text, '') = '';
	exception
		when others then
		qt_horario_w := 0;
	end;

	/* consistir bloqueio dia */

	/*SO-2220426*/

	begin		
	select 	count(*)
	into STRICT	qt_dia_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(ie_classif_bloqueio, coalesce(ie_classif_agenda_p, 'XPTO')) = coalesce(ie_classif_agenda_p, 'XPTO')
	and	coalesce(hr_inicio_bloqueio::text, '') = ''
	and	coalesce(hr_final_bloqueio::text, '') = ''
	and	(ie_dia_semana IS NOT NULL AND ie_dia_semana::text <> '')
	and	((ie_dia_semana = ie_dia_semana_p) or ((ie_dia_semana = 9) and (ie_dia_semana_p not in (1,7))));
	exception
		when others then
		qt_dia_w := 0;
	end;

	/* consistir bloqueio dia e horario */

	/*SO-2220426*/

	begin
	select	count(*)
	into STRICT	qt_dia_hor_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(ie_classif_bloqueio, coalesce(ie_classif_agenda_p, 'XPTO')) = coalesce(ie_classif_agenda_p, 'XPTO')
	and	dt_horario_p between	to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	to_date(to_char(hr_inicio_bloqueio,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') < to_date(to_char(hr_final_bloqueio,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	(hr_inicio_bloqueio IS NOT NULL AND hr_inicio_bloqueio::text <> '')
	and	(hr_final_bloqueio IS NOT NULL AND hr_final_bloqueio::text <> '')
	and	(ie_dia_semana IS NOT NULL AND ie_dia_semana::text <> '')
	and	((ie_dia_semana = ie_dia_semana_p) or ((ie_dia_semana = 9) and (ie_dia_semana_p not in (1,7))));
	exception
		when others then
		qt_dia_hor_w := 0;
	end;
	
	/* verificar horario bloqueado */

	if (qt_periodo_w > 0) or (qt_horario_w > 0) or (qt_dia_w > 0) or (qt_dia_hor_w > 0) then
		ie_bloqueio_w := 'S';
	end if;
end if;

ie_bloqueio_p := ie_bloqueio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
