SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_bloqueio_alta ( cd_motivo_alta_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

ie_possui_regra_w	bigint;

nr_sequencia_w		bigint;
cd_motivo_alta_w	bigint;
ie_tipo_atendimento_w	bigint;
cd_perfil_w		bigint;
cd_perfil_ww		bigint;
ie_tipo_atendimento_ww	bigint;
ie_abortar_w		varchar(1) := 'N';


BEGIN
cd_perfil_ww	:= obter_perfil_ativo;

if (coalesce(cd_motivo_alta_p,0) > 0) and (coalesce(nr_atendimento_p,0) > 0) then

	select 	count(*)
	into STRICT	ie_possui_regra_w
	from	regra_bloqueio_alta
	where	ie_situacao = 'A';

	if (coalesce(ie_possui_regra_w,0) > 0) then

		select	max(ie_tipo_atendimento)
		into STRICT	ie_tipo_atendimento_ww
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p;

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_abortar_w
		from	regra_bloqueio_alta
		where	ie_situacao = 'A'
		and (ie_tipo_atendimento = ie_tipo_atendimento_ww or coalesce(ie_tipo_atendimento::text, '') = '')
		and (cd_perfil = cd_perfil_ww or coalesce(cd_perfil::text, '') = '')
		and (cd_motivo_alta = cd_motivo_alta_p or coalesce(cd_motivo_alta::text, '') = '');


		if (ie_abortar_w = 'S') then
		/*
		Não será possível gerar a alta para este atendimento.
		Favor verificar a regra "Regra de bloqueio de alta"!
		*/
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(237594);
		end if;
	end if;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
