CREATE OR REPLACE PROCEDURE consistir_bloq_agenda_obs (cd_agenda_p bigint, dt_horario_p timestamp, ie_dia_semana_p bigint, ie_bloqueio_p INOUT text, ds_observacao_p INOUT text) AS $body$
DECLARE


ie_bloqueio_w varchar(1) 	:= 'N';
qt_periodo_w	bigint	:= 0;
qt_horario_w	bigint	:= 0;
qt_dia_w	bigint	:= 0;
qt_dia_hor_w	bigint	:= 0;

ds_obs_periodo_w	varchar(255);
ds_obs_horario_w	varchar(255);
ds_obs_dia_w		varchar(255);
ds_obs_dia_hor_w	varchar(255);
nr_seq_bloq_periodo_w	bigint;
nr_seq_bloq_horario_w	bigint;
nr_seq_bloq_dia_w	bigint;
nr_seq_bloq_dia_hor_w	bigint;
qt_dia_inicial_w				bigint;
qt_dia_final_w				bigint;
qt_dia_filtro_ini_w				bigint;
qt_dia_filtro_fim_w				bigint;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') and (ie_dia_semana_p IS NOT NULL AND ie_dia_semana_p::text <> '') then

	qt_dia_inicial_w	:= 2;
	qt_dia_final_w	:= 6;
	qt_dia_filtro_ini_w	:= 7;
	qt_dia_filtro_fim_w	:= 1;

	/* consistir bloqueio dia e horário */

	begin
	select	max(nr_sequencia),
		max(ds_observacao)
	into STRICT	nr_seq_bloq_dia_hor_w,
		ds_obs_dia_hor_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	dt_horario_p between	to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	(hr_inicio_bloqueio IS NOT NULL AND hr_inicio_bloqueio::text <> '')
	and	(hr_final_bloqueio IS NOT NULL AND hr_final_bloqueio::text <> '')
	and	hr_inicio_bloqueio < hr_final_bloqueio
	and	(ie_dia_semana IS NOT NULL AND ie_dia_semana::text <> '')
	and	((ie_dia_semana = ie_dia_semana_p) or ((ie_dia_semana = 9) and (ie_dia_semana_p not in (qt_dia_filtro_ini_w, qt_dia_filtro_fim_w))));
	exception
		when others then
		ds_obs_dia_hor_w := '';
	end;

	if (coalesce(nr_seq_bloq_dia_hor_w, 0) > 0) then
		ie_bloqueio_w := 'S';
		goto final;
	end if;

	/* consistir bloqueio dia */

	begin
	select 	max(nr_sequencia),
		max(ds_observacao)
	into STRICT	nr_seq_bloq_dia_w,
		ds_obs_dia_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(hr_inicio_bloqueio::text, '') = ''
	and	coalesce(hr_final_bloqueio::text, '') = ''
	and	(ie_dia_semana IS NOT NULL AND ie_dia_semana::text <> '')
	and	((ie_dia_semana = ie_dia_semana_p) or ((ie_dia_semana = 9) and (ie_dia_semana_p not in (qt_dia_filtro_ini_w, qt_dia_filtro_fim_w))));
	exception
		when others then
		ds_obs_dia_w := '';
	end;

	if (coalesce(nr_seq_bloq_dia_w, 0) > 0) then
		ie_bloqueio_w := 'S';
		goto final;
	end if;

	/* consistir bloqueio horário */

	begin
	select	max(nr_sequencia),
		max(ds_observacao)
	into STRICT	nr_seq_bloq_horario_w,
		ds_obs_horario_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	dt_horario_p between	to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and
		to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	(hr_inicio_bloqueio IS NOT NULL AND hr_inicio_bloqueio::text <> '')
	and	(hr_final_bloqueio IS NOT NULL AND hr_final_bloqueio::text <> '')
	and	hr_inicio_bloqueio < hr_final_bloqueio
	and	coalesce(ie_dia_semana::text, '') = '';
	exception
		when others then
		ds_obs_horario_w := '';
	end;

	if (coalesce(nr_seq_bloq_horario_w, 0) > 0) then
		ie_bloqueio_w := 'S';
		goto final;
	end if;

	/* consistir bloqueio período */

	begin
	select	max(nr_sequencia),
		max(ds_observacao)
	into STRICT	nr_seq_bloq_periodo_w,
		ds_obs_periodo_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(hr_inicio_bloqueio::text, '') = ''
	and	coalesce(hr_final_bloqueio::text, '') = ''
	and	coalesce(ie_dia_semana::text, '') = '';
	exception
		when others then
		ds_obs_periodo_w := '';
	end;

	if (coalesce(nr_seq_bloq_periodo_w, 0) > 0) then
		ie_bloqueio_w := 'S';
		goto final;
	end if;

end if;

<<final>>

if (ds_obs_periodo_w IS NOT NULL AND ds_obs_periodo_w::text <> '') then
	ds_observacao_p	:= ds_obs_periodo_w;
end if;

if (ds_obs_horario_w IS NOT NULL AND ds_obs_horario_w::text <> '') then
	ds_observacao_p	:= ds_obs_horario_w;
end if;
if (ds_obs_dia_w IS NOT NULL AND ds_obs_dia_w::text <> '') then
	ds_observacao_p	:= ds_obs_dia_w;
end if;
if (ds_obs_dia_hor_w IS NOT NULL AND ds_obs_dia_hor_w::text <> '') then
	ds_observacao_p	:= ds_obs_dia_hor_w;
end if;
ie_bloqueio_p := ie_bloqueio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_bloq_agenda_obs (cd_agenda_p bigint, dt_horario_p timestamp, ie_dia_semana_p bigint, ie_bloqueio_p INOUT text, ds_observacao_p INOUT text) FROM PUBLIC;

