SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_caixa_lote_cartao (nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_acao_p text) AS $body$
DECLARE


vl_lote_w		movto_cartao_cr_parcela.vl_parcela%type;
nr_seq_caixa_movto_w	bigint;
nr_seq_tf_movto_w	bigint;
ie_saldo_negativo_w	varchar(1);
vl_saldo_atual_w	double precision;
vl_saldo_avista_w	double precision	:= 0;
vl_saldo_pre_w		double precision	:= 0;
nr_seq_saldo_caixa_w	bigint;
dt_saldo_w		timestamp;
ie_saldo_caixa_w	transacao_financeira.ie_saldo_caixa%type;
ie_caixa_w		transacao_financeira.ie_caixa%type;
vl_atualizado_w		double precision	:= 0;
ie_saldo_neg_cheque_w			varchar(1);
ie_saldo_dep_cheque_w			varchar(255);


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then

	nr_seq_caixa_movto_w := obter_param_usuario(3020, 34, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, nr_seq_caixa_movto_w);
	nr_seq_tf_movto_w := obter_param_usuario(3020, 35, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, nr_seq_tf_movto_w);

	if (ie_acao_p = 'I') then
		select	coalesce(sum(a.vl_parcela),0)
		into STRICT	vl_lote_w
		from	movto_cartao_cr_parcela a
		where	a.nr_seq_lote	= nr_seq_lote_p;
	else
		select	coalesce(sum(a.vl_parcela),0) *-1
		into STRICT	vl_lote_w
		from	movto_cartao_cr_parcela a
		where	a.nr_seq_lote	= nr_seq_lote_p;
	end if;

	begin
	select	coalesce(ie_saldo_negativo,'S'),
		coalesce(ie_saldo_dep_cheque,'N'),
		coalesce(ie_saldo_neg_cheque,'N')
	into STRICT	ie_saldo_negativo_w,
		ie_saldo_dep_cheque_w,
		ie_saldo_neg_cheque_w
	from	parametro_tesouraria
	where	cd_estabelecimento	= cd_estabelecimento_p;
	exception
	when no_data_found then
		/*Os parâmetros da tesouraria não estão cadastrados!*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(220466);
	end;

	if (nr_seq_caixa_movto_w IS NOT NULL AND nr_seq_caixa_movto_w::text <> '') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_saldo_caixa_w
		from	caixa_saldo_diario
		where	nr_seq_caixa	= nr_seq_caixa_movto_w
		and	coalesce(dt_fechamento::text, '') = '';

	end if;

	if (nr_seq_saldo_caixa_w IS NOT NULL AND nr_seq_saldo_caixa_w::text <> '') then
		select	max(dt_saldo)
		into STRICT	dt_saldo_w
		from	caixa_saldo_diario
		where	nr_sequencia	= nr_seq_saldo_caixa_w;
	end if;

	if (nr_seq_caixa_movto_w IS NOT NULL AND nr_seq_caixa_movto_w::text <> '') and (nr_seq_tf_movto_w IS NOT NULL AND nr_seq_tf_movto_w::text <> '') and (dt_saldo_w IS NOT NULL AND dt_saldo_w::text <> '')then

		if ( coalesce(ie_saldo_negativo_w,'S') = 'N' ) then

			select	max(vl_saldo),
				coalesce(max(vl_cheque_vista_atual),0),
				coalesce(max(vl_cheque_pre_atual),0)
			into STRICT	vl_saldo_atual_w,
				vl_saldo_avista_w,
				vl_saldo_pre_w
			from	caixa_saldo_diario
			where	trunc(dt_saldo,'dd')	= trunc(dt_saldo_w,'dd')
			and	nr_seq_caixa		= nr_seq_caixa_movto_w;

			select 	max(ie_saldo_caixa),
				max(ie_caixa)
			into STRICT	ie_saldo_caixa_w,
				ie_caixa_w
			from	transacao_financeira
			where	nr_sequencia = nr_seq_tf_movto_w;

			if (ie_caixa_w	= 'D') or (ie_caixa_w	= 'T') or (ie_caixa_w	= 'A') then
				if (ie_saldo_caixa_w	= 'S') then
					vl_atualizado_w		:= coalesce(vl_atualizado_w,0) + coalesce(vl_lote_w,0);
				elsif (ie_saldo_caixa_w	= 'E') then
					vl_atualizado_w		:= coalesce(vl_atualizado_w,0) + (coalesce(vl_lote_w,0) * -1);
				end if;
			end if;

			if	((vl_saldo_atual_w - vl_atualizado_w) < 0) then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(220480,'VL_SALDO_ATUAL_W='||vl_saldo_atual_w||';VL_SALDO_FECHAMENTO_W='||(vl_saldo_atual_w - vl_atualizado_w));
			end if;

		end if;

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_caixa_lote_cartao (nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_acao_p text) FROM PUBLIC;

