CREATE OR REPLACE PROCEDURE consistir_campo_obrig_agenda ( cd_agenda_p bigint, qt_peso_p text, qt_altura_cm_p text, cd_medico_req_p text, ie_anestesia_p text, nr_seq_motivo_anest_p text, nr_seq_unid_solic_ext_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ie_peso_w		varchar(1);
ie_altura_w		varchar(1);
ie_requisitante_w		varchar(1);
ie_motivo_anestesia_w	varchar(1);
ie_unidade_externa_w	varchar(1);
ds_campos_w		varchar(255) := '';
ds_enter_w		varchar(10)  :=  chr(13) || chr(10);


BEGIN

select	coalesce(max(ie_peso),'N'),
	coalesce(max(ie_altura),'N'),
	coalesce(max(ie_requisitante),'N'),
	coalesce(max(ie_motivo_anestesia),'N'),
	coalesce(max(ie_unidade_externa),'N')
into STRICT	ie_peso_w,
	ie_altura_w,
	ie_requisitante_w,
	ie_motivo_anestesia_w,
	ie_unidade_externa_w
from	regra_agenda_campo_obrig
where	cd_agenda = cd_agenda_p;

if (ie_peso_w	= 'S' and coalesce(qt_peso_p::text, '') = '') then
	ds_campos_w	:= ds_campos_w ||WHEB_MENSAGEM_PCK.get_texto(279596,null);
end if;

if (ie_altura_w	= 'S' and coalesce(qt_altura_cm_p::text, '') = '') then
	ds_campos_w	:= ds_campos_w ||WHEB_MENSAGEM_PCK.get_texto(279595,null);
end if;

if (ie_requisitante_w	= 'S' and coalesce(cd_medico_req_p::text, '') = '') then
	ds_campos_w	:= ds_campos_w ||WHEB_MENSAGEM_PCK.get_texto(281469,null);
end if;

if (ie_motivo_anestesia_w	= 'S' and coalesce(nr_seq_motivo_anest_p::text, '') = '' and ie_anestesia_p = 'S') then
	ds_campos_w	:= ds_campos_w ||WHEB_MENSAGEM_PCK.get_texto(281471,null);
end if;

if (ie_unidade_externa_w	= 'S' and nr_seq_unid_solic_ext_p = 0) then
	ds_campos_w	:= ds_campos_w ||WHEB_MENSAGEM_PCK.get_texto(281474,null);
end if;

if (ds_campos_w IS NOT NULL AND ds_campos_w::text <> '') then
	ds_erro_p	:= substr(wheb_mensagem_pck.get_texto(281479,null) || ds_enter_w || substr(ds_campos_w,1,length(ds_campos_w||' ')-2),1,255);
end if;

end;
$body$
