CREATE OR REPLACE PROCEDURE consistir_classif_agecons (cd_pessoa_fisica_p text, cd_agenda_p bigint, ie_classif_agenda_p text, dt_agendamento_p timestamp, ds_erro_p INOUT text) AS $body$
DECLARE

qt_regra_classif_w	bigint;
nr_sequencia_w		bigint;
ds_erro_w		varchar(255)	:= '';
cd_classif_princ_w	varchar(15);


BEGIN
select 	max(a.cd_classif_depend)
into STRICT	cd_classif_princ_w
from	regra_classif_dependencia a
where	a.cd_classif_princ = OBTER_CLASSIF_TASY_AGECONS(ie_classif_agenda_p);

if (cd_classif_princ_w IS NOT NULL AND cd_classif_princ_w::text <> '') then
	begin
	select max(nr_sequencia)
	into STRICT   nr_sequencia_w
	from   agenda_consulta
	where  ie_status_agenda = 'E'
	and    OBTER_CLASSIF_TASY_AGECONS(ie_classif_agenda) = cd_classif_princ_w
	and    dt_agendamento   < dt_agendamento_p
	and    cd_agenda        = cd_agenda_p
	and    cd_pessoa_fisica = cd_pessoa_fisica_p;
	end;
		if (coalesce(nr_sequencia_w::text, '') = '') then
			ds_erro_w	:= 	WHEB_MENSAGEM_PCK.get_texto(278851,null);
		end if;
end if;
ds_erro_p	:= ds_erro_w;
end;
$body$
