CREATE OR REPLACE PROCEDURE consistir_classif_med_retag ( nr_seq_classif_medico_p bigint, cd_medico_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_consistir_classif_medico_w		varchar(1) := 'N';
ie_retaguarda_w				varchar(1) := 'N';
ie_retaguarda_medico_w			varchar(1) := 'N';

BEGIN

ie_consistir_classif_medico_w := Obter_Param_Usuario(1002, 148, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consistir_classif_medico_w);

if (ie_consistir_classif_medico_w = 'S') then
	select	coalesce(max(ie_retaguarda),'N')
	into STRICT	ie_retaguarda_w
	from	medico_classif_atend
	where	nr_sequencia = nr_seq_classif_medico_p;

	if (ie_retaguarda_w = 'S') then
		select	coalesce(max(ie_retaguarda),'N')
		into STRICT	ie_retaguarda_medico_w
		from	medico
		where	cd_pessoa_fisica = cd_medico_p;

		if (ie_retaguarda_medico_w = 'N') then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(286749); --O médico não está cadastrado como médico retaguarda no cadastro médico! Parâmetro [148] da função Gestão de Vagas
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
