CREATE OR REPLACE PROCEDURE consistir_componentes_opm ( nr_seq_ordem_opm_p bigint, nr_sequencia_p bigint, cd_material_p bigint, nr_seq_lote_fornec_p bigint, qt_material_p bigint, qt_saldo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_padrao_w	bigint;


BEGIN

select	max(cd_material_padrao)
into STRICT	cd_material_padrao_w
from	ordem_producao_comp_opm
where	nr_seq_ordem_opm 	= nr_seq_ordem_opm_p
and	cd_material 		= cd_material_p
and	coalesce(nr_seq_lote_fornec::text, '') = ''
and	coalesce(ie_status,'G')	= 'G';

insert into ordem_producao_comp_opm(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_ordem_opm,
					nr_seq_lote_fornec,
					cd_material_padrao,
					cd_material,
					qt_material,
					ie_status,
					nr_seq_origem)
values (nextval('ordem_producao_comp_opm_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_ordem_opm_p,
					nr_seq_lote_fornec_p,
					cd_material_padrao_w,
					cd_material_p,
					qt_material_p,
					'C',
					nr_sequencia_p);



commit;

end;
$body$
