SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_comp_lote_producao ( nr_lote_producao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w				integer;
qt_prevista_w				double precision;
cd_local_estoque_w			smallint;
qt_conv_est_Cons_Lote_w			double precision;

qt_real_w					double precision;
qt_estoque_w				double precision;
cd_local_estoque_comp_w			smallint;
nr_sequencia_w				bigint;
cd_material_comp_w			integer;

qt_disp_estoque_atual_w			double precision;

c01 CURSOR FOR
SELECT	a.cd_material,
	CASE WHEN coalesce(a.qt_real,0)=0 THEN  a.qt_prevista  ELSE qt_real END ,
	CASE WHEN coalesce(a.qt_estoque,0)=0 THEN  a.qt_prevista_etq  ELSE qt_estoque END ,
	a.cd_local_estoque,
	a.nr_sequencia
from	lote_producao_comp a
where 	a.nr_lote_producao = nr_lote_producao_p;


BEGIN

select	a.cd_material,
	a.qt_prevista,
	a.cd_local_estoque,
	CASE WHEN a.cd_unidade_medida=substr(obter_dados_material_estab(a.cd_material,a.cd_estabelecimento,'UMS'),1,30) THEN  1  ELSE m.qt_conv_estoque_consumo END
into STRICT	cd_material_w,
	qt_prevista_w,
	cd_local_estoque_w,
	qt_conv_est_cons_lote_w
from	material m,
	lote_producao a
where 	nr_lote_producao = nr_lote_producao_p
and 	a.cd_material = m.cd_material;

delete	FROM consist_comp_lote_prod
where	nr_lote_producao = nr_lote_producao_p;
commit;

open c01;
loop
fetch c01 into
	cd_material_comp_w,
	qt_real_w,
	qt_estoque_w,
	cd_local_estoque_comp_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	/* consiste o saldo do material  */

	select	obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_comp_w, cd_local_estoque_comp_w, clock_timestamp())
	into STRICT	qt_disp_estoque_atual_w
	;

	if (qt_disp_estoque_atual_w < qt_estoque_w) then
		CALL gerar_consist_comp_lote_prod(	nr_lote_producao_p,
						cd_material_comp_w,
						'S',
						wheb_mensagem_pck.get_texto(313578),
						nm_usuario_p,
						qt_disp_estoque_atual_w);
	end if;

	end;
end loop;
close c01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
