CREATE OR REPLACE PROCEDURE consistir_contagem_inventario ( nr_seq_inventario_p bigint, cd_material_p bigint, nr_seq_lote_p bigint default null, ds_mensagem_p text DEFAULT NULL, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


ie_existe_consistencia_w varchar(1) := 'N';

BEGIN

    select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
    into STRICT    ie_existe_consistencia_w
    from    consiste_material_importar
    where   nr_seq_inventario = nr_seq_inventario_p
    and     cd_material = cd_material_p
    and     ds_mensagem = ds_mensagem_p
    and     nm_usuario = nm_usuario_p;

    if (ie_existe_consistencia_w = 'N') then
        insert into consiste_material_importar(nr_seq_inventario, cd_material, nr_seq_lote, ds_mensagem, nm_usuario)
        values (nr_seq_inventario_p, cd_material_p, nr_seq_lote_p, ds_mensagem_p, nm_usuario_p);
        commit;
    end if;

end;
