CREATE OR REPLACE PROCEDURE consistir_conta_atendimento ( nr_atendimento_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

 
vl_parametro_w			varchar(1);
ie_fechar_conta_ps_alta_w	varchar(1);
qt_paciente_w			bigint;
			

BEGIN 
 
vl_parametro_w := obter_param_usuario(916, 323, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);
 
if (vl_parametro_w = 'S')then 
	begin 
	 
	select 	max(ie_fechar_conta_ps_alta) 
	into STRICT	ie_fechar_conta_ps_alta_w 
	from 	parametro_faturamento 
	where 	cd_estabelecimento = cd_estabelecimento_p;
	 
	select 	count(*) 
	into STRICT	qt_paciente_w 
	from 	conta_paciente 
	where 	nr_atendimento = nr_atendimento_p 
	and 	ie_status_acerto = 1;
	 
	if (ie_fechar_conta_ps_alta_w = 'A') and (qt_paciente_w > 0)then 
		begin 
		 
		ds_retorno_p := wheb_mensagem_pck.get_texto(298986);
		 
		end;
	end if;
	 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
