CREATE OR REPLACE PROCEDURE consistir_cotacao_liberada ( nr_cot_compra_p bigint) AS $body$
DECLARE


dt_aprovacao_w			cot_compra.dt_aprovacao%type;
qt_registros_w			bigint;


BEGIN

select	max(dt_aprovacao)
into STRICT	dt_aprovacao_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(733819);
else
	select	count(*)
	into STRICT	qt_registros_w
	from	cot_compra_item
	where	(nr_seq_aprovacao IS NOT NULL AND nr_seq_aprovacao::text <> '')
	and	nr_cot_compra = nr_cot_compra_p;

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(733821);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
