SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_cpoe_liberacao ( ds_itens_liberar_p text, nr_atendimento_p bigint, nm_usuario_p text, cd_perfil_p bigint, ie_consistencias_p INOUT text) AS $body$
DECLARE

 
ds_itens_liberar_w	varchar(4000)	:= ds_itens_liberar_p;
ds_item_w		varchar(255);
ie_tipo_cpoe_w		varchar(20);
nr_seq_item_w		bigint;
ie_tipo_item_w		varchar(15);
ie_erro_liberar_w	varchar(5);
cd_pessoa_fisica_w	cpoe_material.cd_pessoa_fisica%type;
cd_material_w		cpoe_material.cd_material%type;
dt_inicio_w		timestamp;
qt_suspenso_w		integer;
nr_seq_erro_w		bigint;
ie_consistencias_w	varchar(1) := '0';

					 

BEGIN 
 
while(ds_itens_liberar_w IS NOT NULL AND ds_itens_liberar_w::text <> '') loop 
	begin 
	if (position(';' in ds_itens_liberar_w) = 0) and (ds_itens_liberar_w IS NOT NULL AND ds_itens_liberar_w::text <> '') then 
		ds_itens_liberar_w	:= ds_itens_liberar_w||';';
	end if;	
	ds_item_w	:= substr(ds_itens_liberar_w, 1, position(';' in ds_itens_liberar_w) - 1);
	ie_tipo_cpoe_w	:= substr(ds_item_w, 1, position(',' in ds_item_w) - 1);
	nr_seq_item_w	:= (substr(ds_item_w, position(',' in ds_item_w) + 1, length(ds_item_w)))::numeric;
	ds_itens_liberar_w := substr(ds_itens_liberar_w, position(';' in ds_itens_liberar_w) + 1, length(ds_itens_liberar_w));
 
	case ie_tipo_cpoe_w 
		when 'M' then 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_MATERIAL',nr_seq_item_w);
		when 'MA' then 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_MATERIAL',nr_seq_item_w);
		when 'N' then --Nutricao 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_DIETA',nr_seq_item_w);
		when 'P' then --Procedimentos 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_PROCEDIMENTO',nr_seq_item_w);
		when 'G' then --Gasoterapia 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_GASOTERAPIA',nr_seq_item_w);
		when 'R' then --Recomendação 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_RECOMENDACAO',nr_seq_item_w);
		when 'H' then --Hemoterapia 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_HEMOTERAPIA',nr_seq_item_w);
		when 'DI' then --Dialysis 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_DIALISE',nr_seq_item_w);
		when 'DP' then --Dialysis 
			ie_tipo_item_w	:= cpoe_obter_tipo_item('CPOE_DIALISE',nr_seq_item_w);			
	end case;	
	 
	delete	FROM cpoe_inconsistency 
	where	nr_atendimento	= nr_atendimento_p 
	and	nm_usuario	= nm_usuario_p 
	and	nr_seq_item	= nr_seq_item_w 
	and	ie_tipo_item	= ie_tipo_item_w;
	 
	ie_erro_liberar_w	:= obter_lib_erro_regra_rep(18, cd_perfil_p);
	 
	if (ie_tipo_item_w in ('M','SOL')) then 
 
		ie_erro_liberar_w	:= '';
 
		if (wheb_assist_pck.Obter_Se_Consiste_REP(110,cd_perfil_p) = 'S') then 
			begin 
			select	count(1) 
			into STRICT	qt_suspenso_w 
			from	prescr_material		a, 
				prescr_medica		b 
			where	a.cd_material		= cd_material_w 
			and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w 
			and	b.dt_prescricao between PKG_DATE_UTILS.start_of(dt_inicio_w, 'dd', 0) and fim_dia(dt_inicio_w) 
			and	a.nr_prescricao		= b.nr_prescricao 
			and	a.ie_suspenso		= 'S' 
			and	coalesce(a.ie_modificado,'N') <> 'S'  LIMIT 1;
 
			if (qt_suspenso_w > 0) then 
				begin 
				ie_erro_liberar_w	:= obter_lib_erro_regra_rep(110, cd_perfil_p);
				nr_seq_erro_w := gerar_erro_cpoe(nr_seq_item_w, ie_tipo_item_w, 110, ie_erro_liberar_w, null, cd_perfil_p, nm_usuario_p, nr_atendimento_p, null, nr_seq_erro_w);
				if (ie_consistencias_w <> 'N') then 
					ie_consistencias_w	:= ie_erro_liberar_w;
				end if;
				end;
			end if;
			end;
		end if;			
	end if;
 
	end;
end loop;
 
ie_consistencias_p	:= ie_consistencias_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_cpoe_liberacao ( ds_itens_liberar_p text, nr_atendimento_p bigint, nm_usuario_p text, cd_perfil_p bigint, ie_consistencias_p INOUT text) FROM PUBLIC;

