SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_cracha_paciente ( nr_controle_p text, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

qt_atend_w		integer;
ie_cracha_sem_saida_w	varchar(1);
nr_atendimento_w	bigint;
qt_acompanhantes_w	bigint;
qt_visitantes_w		bigint;

BEGIN

ie_cracha_sem_saida_w := Obter_param_Usuario(8014, 101, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_cracha_sem_saida_w);
if (ie_cracha_sem_saida_w = 'N') then

	if (cd_setor_atendimento_p > 0) then
		select	count(*)
		into STRICT	qt_atend_w
		from	unidade_atend_cracha
		where	cd_setor_atendimento 	= cd_setor_atendimento_p
		and	cd_unidade_basica	= cd_unidade_basica_p
		and	cd_unidade_compl	= cd_unidade_compl_p
		and	cd_cracha		= nr_controle_p;

		if (qt_atend_w = 0) then
			ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278171,null) || substr(obter_nome_setor(cd_setor_atendimento_p),1,80);
		end if;
	end if;
else

	if (cd_setor_atendimento_p > 0) then
		select	count(*)
		into STRICT	qt_atend_w
		from	unidade_atend_cracha
		where	cd_setor_atendimento 	= cd_setor_atendimento_p
		and	cd_unidade_basica	= cd_unidade_basica_p
		and	cd_unidade_compl	= cd_unidade_compl_p
		and	cd_cracha		= nr_controle_p;

		if (qt_atend_w = 0) then
			ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278171,null) || substr(obter_nome_setor(cd_setor_atendimento_p),1,80);
		else

			select 	count(*)
			into STRICT	qt_visitantes_w
			from	atendimento_visita a
			where	coalesce(dt_saida::text, '') = ''
			and	coalesce(ie_paciente,'N') = 'N'
			and	nr_seq_controle = nr_controle_p;

			select 	count(*)
			into STRICT	qt_acompanhantes_w
			from	atendimento_acompanhante a
			where	coalesce(dt_saida::text, '') = ''
			and	nr_controle = nr_controle_p;

			if (coalesce(qt_visitantes_w,0) > 0) or (coalesce(qt_acompanhantes_w,0) > 0) then

				ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(278172,null);

			end if;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
