CREATE OR REPLACE PROCEDURE consistir_credenciamento_pf (cd_estabelecimento_p bigint, cd_especialidade_p text, cd_paciente_p text, dt_referencia_p timestamp, cd_pf_credenciada_p text, nr_atendimento_p bigint, nr_atendimento_mae_p bigint default null, ie_pf_credenciada_p INOUT text DEFAULT NULL) AS $body$
DECLARE


ie_pf_credenciada_w	varchar(2);
qt_credenciamento_w	bigint;
qt_recem_nascido_w	bigint;


BEGIN
if (cd_pf_credenciada_p IS NOT NULL AND cd_pf_credenciada_p::text <> '') then

	/* obter se regras pf */

	select	count(*)
	into STRICT	qt_credenciamento_w
	from	pf_credenciamento
	where	cd_pf_credenciamento	= cd_pf_credenciada_p
	and	((cd_estabelecimento	= cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''));

	/* verificar regras pf */

	if (qt_credenciamento_w > 0) then

		select 	count(*)
		into STRICT 	qt_recem_nascido_w
		from	nascimento
		where 	nr_atendimento = nr_atendimento_mae_p;


		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_pf_credenciada_w
		from	pf_credenciamento
		where	cd_pf_credenciamento				= cd_pf_credenciada_p
		and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
		--and	((cd_especialidade_p = 0) or (nvl(cd_especialidade,cd_especialidade_p)	= cd_especialidade_p))
		and	((coalesce(cd_paciente,coalesce(cd_paciente_p,'0'))		= coalesce(cd_paciente_p,'0')) or ((coalesce(ie_credenciar_rn,'N') = 'S') and (qt_recem_nascido_w > 0)))
		and	((coalesce(nr_atendimento, coalesce(nr_atendimento_p,0))	= coalesce(nr_atendimento_p,0)) or ((coalesce(ie_credenciar_rn,'N') = 'S') and (qt_recem_nascido_w > 0)))
		and	consistir_vigencia_credenc(dt_credenciamento,dt_fim_credenciamento,dt_referencia_p)	= 'S';


	else

		ie_pf_credenciada_w	:= 'NC';

	end if;

end if;

ie_pf_credenciada_p := ie_pf_credenciada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
