CREATE OR REPLACE PROCEDURE consistir_dados_agenda_serv ( cd_agenda_p bigint, cd_pessoa_fisica_p text, nr_seq_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ie_possui_agendamento_p INOUT text, ds_msg_abort_p INOUT text, ds_msg_pergunta_p INOUT text) AS $body$
DECLARE

 
ie_consiste_horario_agendas_w	varchar(2);
ie_exige_pf_w		varchar(1);


BEGIN 
 
select	coalesce(max(ie_exige_pessoa_fisica),'N') 
into STRICT	ie_exige_pf_w 
from	agenda 
where	cd_agenda	= cd_agenda_p;
 
if (ie_exige_pf_w = 'S') and (coalesce(cd_pessoa_fisica_p::text, '') = '') then 
	ds_msg_abort_p	:= substr(obter_texto_tasy(47445, wheb_usuario_pck.get_nr_seq_idioma),1,255);
end if;
 
if (coalesce(ds_msg_abort_p::text, '') = '') then 
	select	substr(obter_se_pac_agendamento(nr_seq_agenda_p, cd_pessoa_fisica_p, cd_agenda_p, dt_agenda_p), 1,1) 
	into STRICT	ie_possui_agendamento_p 
	;
 
	-- Agenda de Serviços - Parâmetro [52] - Consistir horário de agendamento em outras agendas 
	ie_consiste_horario_agendas_w := obter_param_usuario(866, 52, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_horario_agendas_w);
 
	if (ie_possui_agendamento_p = 'S') then 
		if (ie_consiste_horario_agendas_w = 'S') then 
			ds_msg_pergunta_p	:= substr(obter_texto_tasy(47449, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		elsif (ie_consiste_horario_agendas_w = 'MA') then 
			ds_msg_pergunta_p	:= substr(obter_texto_tasy(736855, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
