CREATE OR REPLACE PROCEDURE consistir_desdobr_entrega_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, qt_total_entrega_p bigint, dt_prevista_entrega_p timestamp, ie_tipo_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_real_entrega_w				double precision;
qt_material_w				double precision;
qt_prevista_entrega_toal_w			double precision;
qt_cancelados_w				double precision;
ds_erro_w				varchar(255) := '';
nr_sequencia_w				bigint;
qt_existe_inspecao_w			bigint;
qt_registros_w				bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	ordem_compra_item_entrega
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p
	and	coalesce(qt_real_entrega,0) > 0;

BEGIN

if (ie_tipo_p = 1) then
	begin
	if (trunc(dt_prevista_entrega_p) < trunc(clock_timestamp())) then
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(277229) || to_char(dt_prevista_entrega_p,'dd/mm/yyyy') || WHEB_MENSAGEM_PCK.get_texto(277230),1,255);
	end if;

	select	coalesce(sum(qt_real_entrega),0)
	into STRICT	qt_real_entrega_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p
	and	trunc(dt_prevista_entrega,'dd') = dt_prevista_entrega_p;

	if (qt_real_entrega_w > 0) then
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(277231) || to_char(dt_prevista_entrega_p,'dd/mm/yyyy') || WHEB_MENSAGEM_PCK.get_texto(277232),1,255);
	end if;
	end;

elsif (ie_tipo_p = 2) then
	begin

	select	count(1)
	into STRICT	qt_registros_w
	from	inspecao_recebimento a,
		inspecao_contagem c,
		inspecao_registro d,
		ordem_compra_item_entrega b
	where	a.nr_seq_entrega	= b.nr_sequencia
	and	c.nr_seq_inspecao	= a.nr_sequencia
	and 	a.nr_seq_registro	= d.nr_sequencia
	and	b.nr_ordem_compra	= nr_ordem_compra_p
	and	b.nr_item_oci		= nr_item_oci_p
	and	coalesce(d.nr_seq_nf::text, '') = ''
	and	coalesce(d.dt_devolucao::text, '') = '';

	if (qt_registros_w > 0) then
		ds_erro_w	:= substr(WHEB_MENSAGEM_PCK.get_texto(277233),1,255);
	else

		select	count(1)
		into STRICT	qt_registros_w
		from	inspecao_recebimento a,
			inspecao_contagem c,
			inspecao_registro d,
			nota_fiscal n,
			ordem_compra_item_entrega b
		where	a.nr_seq_entrega	= b.nr_sequencia
		and	c.nr_seq_inspecao	= a.nr_sequencia
		and 	a.nr_seq_registro	= d.nr_sequencia
		and	n.nr_sequencia		= d.nr_seq_nf
		and	b.nr_ordem_compra	= nr_ordem_compra_p
		and	b.nr_item_oci		= nr_item_oci_p
		and	coalesce(n.dt_atualizacao_estoque::text, '') = '';

		if (qt_registros_w > 0) then
			ds_erro_w	:= substr(WHEB_MENSAGEM_PCK.get_texto(277233),1,255);
		end if;
	end if;


	/*select	count(*)
	into	qt_existe_inspecao_w
	from	inspecao_recebimento a,
		ordem_compra_item_entrega b
	where	a.nr_seq_entrega	= b.nr_sequencia
	and	b.nr_ordem_compra	= nr_ordem_compra_p
	and	b.nr_item_oci		= nr_item_oci_p
	and	nvl(b.qt_real_entrega,0)	= 0
	and	ie_motivo_devolucao is null
	and	b.dt_cancelamento is null;*/
	/*open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	exit when C01%notfound;
		begin
		update	ordem_compra_item_entrega
		set	qt_prevista_entrega = qt_real_entrega
		where	nr_sequencia = nr_sequencia_w;
		end;
	end loop;
	close C01;*/
	select	coalesce(sum(qt_real_entrega),0)
	into STRICT	qt_real_entrega_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p
	and	coalesce(dt_cancelamento::text, '') = '';

	select	coalesce(sum(coalesce(qt_real_entrega_w,0) + coalesce(qt_prevista_entrega,0)),qt_real_entrega_w)
	into STRICT	qt_real_entrega_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p
	and	coalesce(dt_cancelamento::text, '') = ''
	and	coalesce(qt_real_entrega,0) = 0
	and	(dt_real_entrega IS NOT NULL AND dt_real_entrega::text <> '');

	select	coalesce(sum(qt_prevista_entrega),0)
	into STRICT	qt_material_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p
	and	coalesce(ie_cancelada_inspecao,'N') = 'N';

	select	coalesce(sum(qt_prevista_entrega),0)
	into STRICT	qt_cancelados_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p
	and	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '')
	and	coalesce(qt_real_entrega, 0) = 0
	and	coalesce(ie_cancelada_inspecao,'N') = 'N';

	if	((qt_real_entrega_w + qt_total_entrega_p) <> (qt_material_w - qt_cancelados_w)) then
		ds_erro_w := substr(	WHEB_MENSAGEM_PCK.get_texto(277234)	|| Campo_Mascara((qt_material_w - qt_cancelados_w - qt_real_entrega_w),4)	|| chr(13) ||
					WHEB_MENSAGEM_PCK.get_texto(277235) 	|| Campo_Mascara(qt_total_entrega_p,4),1,255);
		rollback;
	end if;
	end;
end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
