CREATE OR REPLACE PROCEDURE consistir_devolucao_tit ( nr_titulo_receber_p bigint, nr_titulo_pagar_p bigint, nr_sequencia_p bigint, dt_devolucao_p timestamp, dt_retorno_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_devolucao_w	timestamp;
dt_retorno_w	timestamp;


BEGIN

if (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') then
	select	max(dt_devolucao)
	into STRICT	dt_devolucao_w
	from	titulo_receber_devolucao a
	where	a.nr_titulo	= nr_titulo_receber_p
	and	a.nr_sequencia	<> coalesce(nr_sequencia_p,0);

	if (dt_devolucao_p < dt_devolucao_w) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265779,'');
		--Mensagem: Não é possível informar uma data de devolução inferior a uma já existente! Por favor verifique.
	end if;

	select	max(dt_reenvio)
	into STRICT	dt_retorno_w
	from	titulo_receber_devolucao a
	where	a.nr_titulo	= nr_titulo_receber_p
	and	a.nr_sequencia	<> coalesce(nr_sequencia_p,0);

	if (dt_devolucao_w IS NOT NULL AND dt_devolucao_w::text <> '') and (coalesce(dt_retorno_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265780,'');
		--Mensagem: A última devolução precisa ser reenviada para devolver a fatura novamente.
	end if;

elsif (nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') then
	select	max(dt_devolucao)
	into STRICT	dt_devolucao_w
	from	titulo_pagar_devolucao a
	where	a.nr_titulo	= nr_titulo_pagar_p
	and	a.nr_sequencia	<> coalesce(nr_sequencia_p,0);

	if (dt_devolucao_p < dt_devolucao_w) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265781,'');
		--Mensagem: Não é possível informar uma data de devolução inferior a uma já existente! Por favor verifique.
	end if;

	select	max(dt_retorno)
	into STRICT	dt_retorno_w
	from	titulo_pagar_devolucao a
	where	a.nr_titulo	= nr_titulo_pagar_p
	and	a.nr_sequencia	<> coalesce(nr_sequencia_p,0);

	if (dt_devolucao_w IS NOT NULL AND dt_devolucao_w::text <> '') and (coalesce(dt_retorno_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265783,'');
		--Mensagem: A última devolução precisa ser retornada para devolver a fatura novamente.
	end if;
end if;

end;
$body$
