CREATE OR REPLACE PROCEDURE consistir_dia_medic_composto ( cd_protocolo_p bigint, nr_sequencia_p bigint, nr_seq_material_p bigint, ie_agrupador_p bigint, nr_agrupamento_p bigint, ds_dias_aplicacao_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	protocolo_medic_material
where	cd_protocolo		= cd_protocolo_p
and	nr_sequencia		= nr_sequencia_p
and	nr_seq_material		<> nr_seq_material_p
and	ie_agrupador		= ie_agrupador_p
and	nr_agrupamento		= nr_agrupamento_p
and	coalesce(replace(ds_dias_aplicacao,' ',''),'X')	<> coalesce(replace(ds_dias_aplicacao_p,' ',''),'X');

if (qt_registro_w > 0) then
	ds_retorno_p	:= substr(obter_texto_tasy(1197448, wheb_usuario_pck.get_nr_seq_idioma),1,255);
end if;

end;
$body$
