SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_dispensacao_farmacia (nr_prescricao_p bigint) AS $body$
DECLARE


ie_libera_w			varchar(1);
ie_prescr_cpoe_w	varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	coalesce(max('S'), 'N')
	into STRICT	ie_prescr_cpoe_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p
	and		cd_funcao_origem = 2314;

	if (ie_prescr_cpoe_w = 'N') then

	    select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_libera_w
		from	prescr_material a
		where	nr_prescricao		= nr_prescricao_p
		and		ie_agrupador		in (1,4,5,8)
		and		coalesce(dt_suspensao::text, '') = ''
		and		coalesce(ie_suspenso,'N')	= 'N'
		and		coalesce(ie_regra_disp,'S') 	= 'U'
		and		coalesce(ie_administrar,'S') = 'S';

	    if (ie_libera_w = 'N') then
		/*(-20011,	'Esta prescrição possui itens com dispensação à definir.' || chr(10) ||
						'Você não tem autorização para liberar prescrições nestas    condições.' || chr(10) ||
						'Favor verificar.#@#@');*/
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(263534);
		end if;

	else

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_libera_w
		from	prescr_material a,
				cpoe_material b
		where	a.nr_seq_mat_cpoe   = b.nr_sequencia
		and		a.nr_prescricao		= nr_prescricao_p
		and		a.ie_agrupador		in (1,4,5)
		and		coalesce(a.ie_regra_disp,'S') 	= 'U'
		and		coalesce(a.ie_administrar,'S') = 'S'
		and (coalesce(b.dt_suspensao::text, '') = '' or  b.dt_suspensao > trunc(clock_timestamp(),'mi'));

		 if (ie_libera_w = 'N') then
		/*(-20011,	'Esta prescrição possui itens com dispensação à definir.' || chr(10) ||
						'Você não tem autorização para liberar prescrições nestas    condições.' || chr(10) ||
						'Favor verificar.#@#@');*/
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(263534);
		end if;

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_libera_w
		from	prescr_material a,
				cpoe_material b
		where	a.nr_seq_dieta_cpoe = b.nr_sequencia
		and		a.nr_prescricao		= nr_prescricao_p
		and		a.ie_agrupador		= 8
		and		coalesce(a.ie_regra_disp,'S') 	= 'U'
		and		coalesce(a.ie_administrar,'S') = 'S'
		and (coalesce(b.dt_suspensao::text, '') = '' or  b.dt_suspensao > trunc(clock_timestamp(),'mi'));

		 if (ie_libera_w = 'N') then
		/*(-20011,	'Esta prescrição possui itens com dispensação à definir.' || chr(10) ||
						'Você não tem autorização para liberar prescrições nestas    condições.' || chr(10) ||
						'Favor verificar.#@#@');*/
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(263534);
		end if;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_dispensacao_farmacia (nr_prescricao_p bigint) FROM PUBLIC;

