CREATE OR REPLACE PROCEDURE consistir_doacao_coleta_hemot ( nr_seq_doacao_p bigint, qt_hemoglobina_p bigint, pr_hematocrito_p bigint, cd_pessoa_fisica_p text, ie_braco_puncionado_p text, ie_braco_puncionado_old_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_atributo_focus_p INOUT text, ds_mens_hemat_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE


ie_atualiza_braco_pulsiona_p	varchar(1);
qt_dias_inaptidao_w		san_regra_sinal_vital.qt_dias_inapto%type;


BEGIN

SELECT * FROM consistir_hemoglobina_hemot(
	qt_hemoglobina_p, cd_pessoa_fisica_p, cd_estabelecimento_p, nm_usuario_p, ds_atributo_focus_p, ds_mens_hemat_p, ds_erro_p, qt_dias_inaptidao_w) INTO STRICT ds_atributo_focus_p, ds_mens_hemat_p, ds_erro_p, qt_dias_inaptidao_w;

if (coalesce(ds_erro_p::text, '') = '' and coalesce(ds_mens_hemat_p::text, '') = '') then
	begin
	SELECT * FROM consistir_hematocritos_hemot(
		pr_hematocrito_p, cd_pessoa_fisica_p, cd_estabelecimento_p, nm_usuario_p, ds_atributo_focus_p, ds_mens_hemat_p, ds_erro_p, qt_dias_inaptidao_w) INTO STRICT ds_atributo_focus_p, ds_mens_hemat_p, ds_erro_p, qt_dias_inaptidao_w;
	end;
end if;

if (coalesce(ds_erro_p::text, '') = '') then
	begin
	if (ie_braco_puncionado_p IS NOT NULL AND ie_braco_puncionado_p::text <> '') and (ie_braco_puncionado_p <> ie_braco_puncionado_old_p) then
		begin
		/* Hemoterapia - Parametro [156] - Permite atualizar braco puncionado */

		ie_atualiza_braco_pulsiona_p := obter_param_usuario(450, 156, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_braco_pulsiona_p);

		if (ie_atualiza_braco_pulsiona_p = 'S') then
			begin
			CALL san_atualizar_braco_pulsionado(
				nr_seq_doacao_p,
				ie_braco_puncionado_p,
				nm_usuario_p);
			end;
		end if;
		end;
	end if;
	end;
end if;

end;
$body$
