CREATE OR REPLACE PROCEDURE consistir_duracao_agecons (cd_agenda_p bigint, dt_agenda_p timestamp, nr_minuto_duracao_p bigint, nr_seq_agenda_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w	varchar(250)	:= null;
qt_agenda_w	bigint	:= 0;
dt_agenda_w	timestamp;


BEGIN

dt_agenda_w	:= to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || to_char(dt_agenda_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

select	count(*)
into STRICT	qt_agenda_w
from	agenda_consulta
where	cd_agenda		= cd_agenda_p
and	not ie_status_agenda	in ('C', 'L', 'B')
and	((dt_agenda		between dt_agenda_w and dt_agenda_w + ((nr_minuto_duracao_p - 1) / 1440)) or
	((dt_agenda_w		between dt_agenda and dt_agenda + ((nr_minuto_duracao - 1) / 1440))))
and	nr_sequencia		<> nr_seq_agenda_p;

if (qt_agenda_w > 0) then
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(278169,null);
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
