SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_duracao_agenda_pac (cd_agenda_p bigint, dt_agenda_p timestamp, hr_inicio_p timestamp, nr_minuto_duracao_p bigint, nr_seq_agenda_p bigint, nr_seq_origem_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w	varchar(250)	:= null;
qt_agenda_w	bigint	:= 0;
dt_agenda_w	timestamp;
ds_lista_status_w varchar(255)	:= null;


BEGIN

dt_agenda_w	:= to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || to_char(hr_inicio_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

if (wheb_usuario_pck.get_cd_funcao = 871) then /*so ira buscar o parametro se estiver na funcao Gestao da Agenda cirurgica*/
	ds_lista_status_w := Obter_Param_Usuario(871, 820, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ds_lista_status_w);	
end if;

if (coalesce(ds_lista_status_w,'XPTO') = 'XPTO') then	
	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_paciente
	where	cd_agenda		= cd_agenda_p
	and		not ie_status_agenda in ('C', 'L', 'B')
	and	dt_agenda		= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_agenda_w)
	and	((hr_inicio		between dt_agenda_w and dt_agenda_w + ((nr_minuto_duracao_p - 1) / 1440)) or
		((dt_agenda_w		between hr_inicio and hr_inicio + ((nr_minuto_duracao - 1) / 1440))))
	and	coalesce(obter_tipo_classif_agenda(nr_seq_classif_agenda),'XX') <> 'E' /*Dalcastagne em 17/08/2007 OS 64969*/
   and   nr_sequencia      <> coalesce(nr_seq_origem_p, 0)
	and	nr_sequencia		<> nr_seq_agenda_p;
else
	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_paciente
	where	cd_agenda		= cd_agenda_p
	and		obter_se_contido_char(ie_status_agenda,ds_lista_status_w || ',L') = 'N'
	and	dt_agenda		= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_agenda_w)
	and	((hr_inicio		between dt_agenda_w and dt_agenda_w + ((nr_minuto_duracao_p - 1) / 1440)) or
		((dt_agenda_w		between hr_inicio and hr_inicio + ((nr_minuto_duracao - 1) / 1440))))
	and	coalesce(obter_tipo_classif_agenda(nr_seq_classif_agenda),'XX') <> 'E' /* Dalcastagne em 17/08/2007 OS 64969*/
   and   nr_sequencia      <> coalesce(nr_seq_origem_p, 0)
	and	nr_sequencia		<> nr_seq_agenda_p;
end if;	
	

if (qt_agenda_w > 0) then
	/*Ocorreu sobreposicao de horario na agenda, verifique o tempo de duracao!*/

	ds_erro_w	:= wheb_mensagem_pck.get_texto(279113);
end if;	

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_duracao_agenda_pac (cd_agenda_p bigint, dt_agenda_p timestamp, hr_inicio_p timestamp, nr_minuto_duracao_p bigint, nr_seq_agenda_p bigint, nr_seq_origem_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

