CREATE OR REPLACE PROCEDURE consistir_duracao_agenda_tasy (dt_agenda_p timestamp, qt_min_duracao_p bigint, nr_seq_local_p bigint, nr_seq_agenda_p bigint, nm_usuario_agenda_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_agenda_w	bigint;


BEGIN
ds_erro_p := '';
if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (qt_min_duracao_p IS NOT NULL AND qt_min_duracao_p::text <> '') then
	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	agenda_tasy
	where	((dt_agenda between dt_agenda_p and dt_agenda_p + ((qt_min_duracao_p - 1) / 1440)) or
		((dt_agenda_p between dt_agenda and dt_agenda + ((nr_minuto_duracao - 1) / 1440))))
	and	ie_status = 'M'
	and	((nr_seq_local = nr_seq_local_p) or (coalesce(nr_seq_local_p::text, '') = ''))
	and	((nm_usuario_agenda = nm_usuario_agenda_p) or (coalesce(nm_usuario_agenda_p::text, '') = ''))
	and	nr_sequencia <> nr_seq_agenda_p;

	if (qt_agenda_w > 0) then
		ds_erro_p := wheb_mensagem_pck.get_texto(278794);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
