SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_duracao_age_cons (nr_minuto_duracao_p bigint, dt_agenda_p timestamp, cd_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agenda_p bigint, ie_consiste_duracao_p INOUT text, ds_sobreposicao_p INOUT text) AS $body$
DECLARE


nr_minuto_duracao_w		bigint;				
hr_param_w			timestamp;
qt_agendado_w			bigint;
ie_sobreposicao_encaixe_w	varchar(1) := 'S';
			

BEGIN

select	coalesce(max(ie_sobreposicao_encaixe),'S')
into STRICT	ie_sobreposicao_encaixe_w
from	parametro_agenda
where	cd_estabelecimento = cd_estabelecimento_p;

hr_param_w := dt_agenda_p + nr_minuto_duracao_p / 1440;

select	sum(nr_minuto_duracao)
into STRICT	nr_minuto_duracao_w
from	agenda_consulta
where	cd_agenda = cd_agenda_p
and	dt_agenda between dt_agenda_p and hr_param_w
and	((ie_status_agenda  = 'L') OR nr_sequencia = nr_seq_agenda_p);

if (nr_minuto_duracao_w < nr_minuto_duracao_p) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262562);
end if;

select	count(*)
into STRICT	qt_agendado_w
from	agenda_consulta
where	cd_agenda = cd_agenda_p
and	dt_agenda between dt_agenda_p and hr_param_w - 1/1440
and	((ie_sobreposicao_encaixe_w = 'N' and coalesce(ie_encaixe,'N') <> 'S' ) or ie_sobreposicao_encaixe_w = 'S')
and (ie_status_agenda not in ('C','L','B','F','I','II'))
AND 	nr_sequencia <> nr_seq_agenda_p;

if (qt_agendado_w > 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262562);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
