SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_entrega_cot_compra ( nr_cot_compra_p bigint) AS $body$
DECLARE


nr_item_cot_compra_w		bigint;
qt_material_w			double precision;
qt_material_entrega_w	double precision;
ds_erro_w			varchar(2000);

c01 CURSOR FOR
SELECT	a.nr_item_cot_compra,
	a.qt_material,
	coalesce(sum(b.qt_entrega),0)
FROM cot_compra_item a
LEFT OUTER JOIN cot_compra_item_entrega b ON (a.nr_cot_compra = b.nr_cot_compra AND a.nr_item_cot_compra = b.nr_item_cot_compra)
WHERE a.nr_cot_compra = nr_cot_compra_p group by a.nr_item_cot_compra,
	a.qt_material
having a.qt_material <> coalesce(sum(b.qt_entrega),0);


BEGIN

open c01;
loop
fetch c01 into
	nr_item_cot_compra_w,
	qt_material_w,
	qt_material_entrega_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_erro_w := (ds_erro_w || nr_item_cot_compra_w || ' - ');
end loop;
close c01;

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265957,'DS_ERRO=' || substr(substr(ds_erro_w,1,2000),1,length(substr(ds_erro_w ,1,2000))-2));
	--'A quantidade do(s) item(ns): ' || substr(substr(ds_erro_w,1,2000),1,length(substr(ds_erro_w ,1,2000))-2) ||' n√£o confere com as entregas.'
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
