CREATE OR REPLACE PROCEDURE consistir_entrega_item ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, dt_prevista_entrega_p timestamp) AS $body$
DECLARE


qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	ordem_compra_item_entrega
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p
and	trunc(dt_prevista_entrega) = trunc(dt_prevista_entrega_p)
and	coalesce(dt_cancelamento::text, '') = '';

if (qt_registro_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265958);
	--'Ja existe uma entrega pendente deste item para esta data.'
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
