CREATE OR REPLACE PROCEDURE consistir_estorno_material ( nm_usuario_p text, cd_estabelecimento_p bigint, nr_sequencia_p bigint, nr_prescricao_p bigint, ds_erro_p INOUT text ) AS $body$
DECLARE

 
vl_param_w			varchar(15);
ds_erro_w 			varchar(500)	:= NULL;
cd_setor_atend_usuario_w	bigint;	
cd_local_estoque_usuario_w	bigint;
cd_local_estoque_prescr_w	bigint;
qt_devolvida_w		double precision;
ie_fim_conta_w		varchar(1);
dt_fim_conta_w		timestamp;
nr_atendimento_w		bigint;

/** 
 *	Procedure que consiste o estorno do material 
 *	Juliane - 29/03/2007 
 */
 
 
 

BEGIN 
 
Select	cd_setor_atendimento 
into STRICT	cd_setor_atend_usuario_w 
from	usuario 
where	nm_usuario	=	nm_usuario_p;
 
select	cd_local_estoque, 
	qt_devolvida, 
	nr_atendimento 
into STRICT	cd_local_estoque_prescr_w, 
	qt_devolvida_w, 
	nr_atendimento_w 
from	material_atend_paciente 
where	nr_prescricao	=	nr_prescricao_p 
and	nr_sequencia	=	nr_sequencia_p;
 
select	obter_local_estoque_setor(cd_setor_atend_usuario_w, cd_estabelecimento_p) 
into STRICT	cd_local_estoque_usuario_w
;
 
-- 91 - Permite voltar a condição de pendente itens atendidos com local diferente do local do usuário -- 
 
select	obter_valor_param_usuario(36,91,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_p) 
into STRICT	vl_param_w
;
 
if (vl_param_w = 'N') and (cd_local_estoque_prescr_w != cd_local_estoque_usuario_w) then 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277602,null);
end if;
 
-- 29 -	Permite voltar itens devolvidos à condição pendente 
 
select	obter_valor_param_usuario(36, 29,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_p) 
into STRICT	vl_param_w
; 	
 
if (vl_param_w = 'N') and (qt_devolvida_w IS NOT NULL AND qt_devolvida_w::text <> '') then 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277603,null);
end if;
 
-- Verificar se a conta está aberta 
 
select	ie_fim_conta, 
	dt_fim_conta 
into STRICT	ie_fim_conta_w, 
	dt_fim_conta_w 
from	atendimento_paciente a 
where	nr_atendimento		= nr_atendimento_w;
		 
if	(ie_fim_conta_w = 'F' AND dt_fim_conta_w IS NOT NULL AND dt_fim_conta_w::text <> '') then 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277605,null);	
end if;
 
ds_erro_p := ds_erro_w;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_estorno_material ( nm_usuario_p text, cd_estabelecimento_p bigint, nr_sequencia_p bigint, nr_prescricao_p bigint, ds_erro_p INOUT text ) FROM PUBLIC;

