SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_etiqueta_processo (nr_seq_processo_p bigint, nr_etiqueta_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


ds_consistencia_w	varchar(240) := '';
nr_seq_pmh_w		numeric(20);
nr_seq_digito_w		smallint;
ie_etiq_valida_w	varchar(1);
ie_etiq_processo_w	varchar(1);
nr_seq_frac_w		numeric(20);
dt_suspensao_w		timestamp;


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (nr_etiqueta_p IS NOT NULL AND nr_etiqueta_p::text <> '') then

	if (length(nr_etiqueta_p) = 12) and (substr(nr_etiqueta_p,1,1) = '9') then
		begin
		nr_seq_frac_w	:= (substr(nr_etiqueta_p,2,10))::numeric;
		nr_seq_digito_w	:= (substr(nr_etiqueta_p,12,1))::numeric;

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_etiq_valida_w
		from	adep_processo_frac
		where	nr_sequencia	= nr_seq_frac_w
		and	nr_seq_digito	= nr_seq_digito_w;

		if (ie_etiq_valida_w = 'S') then

			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_etiq_processo_w
			from	prescr_mat_hor
			where	nr_seq_processo	= nr_seq_processo_p
			and	nr_seq_etiqueta	= nr_seq_frac_w
			and	coalesce(nr_seq_superior::text, '') = ''
			and	Obter_se_horario_liberado(dt_lib_horario,dt_horario) = 'S';

			if (ie_etiq_processo_w <> 'N') then
				select	max(dt_suspensao)
				into STRICT	dt_suspensao_w
				from	prescr_mat_hor
				where	nr_seq_processo	= nr_seq_processo_p
				and	nr_seq_etiqueta	= nr_seq_frac_w
				and	coalesce(nr_seq_superior::text, '') = ''
				and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
			end if;

			if (ie_etiq_processo_w = 'N') then
				ds_consistencia_w	:= obter_desc_expressao(346373)/*'Esta etiqueta de ordem de manipulação/fracionamento não corresponde a este processo!'*/
 || chr(10) || 'Favor verificar.';
			elsif (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
				ds_consistencia_w	:= obter_desc_expressao(346363);--'Este horário esta suspenso na prescrição!';
			end if;
		else
			ds_consistencia_w	:= obter_desc_expressao(346372)/*'Etiqueta inválida! Este número de ordem de manipulação/fracionamento não existe!'*/
 || chr(10) || 'Favor verificar.';
		end if;
		end;
	else
		begin
		nr_seq_pmh_w	:= substr(nr_etiqueta_p,1,length(nr_etiqueta_p)-1);
		nr_seq_digito_w	:= substr(nr_etiqueta_p,length(nr_etiqueta_p),1);

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_etiq_valida_w
		from	prescr_mat_hor
		where	nr_sequencia	= nr_seq_pmh_w
		and	nr_seq_digito	= nr_seq_digito_w
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

		if (ie_etiq_valida_w = 'S') then

			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_etiq_processo_w
			from	prescr_mat_hor
			where	nr_sequencia	= nr_seq_pmh_w
			and	nr_seq_digito	= nr_seq_digito_w
			and	nr_seq_processo	= nr_seq_processo_p
			and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

			if (ie_etiq_processo_w <> 'N') then
				select	max(dt_suspensao)
				into STRICT	dt_suspensao_w
				from	prescr_mat_hor
				where	nr_sequencia	= nr_seq_pmh_w
				and	nr_seq_digito	= nr_seq_digito_w
				and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
			end if;

			if (ie_etiq_processo_w = 'N') then
				ds_consistencia_w	:= wheb_mensagem_pck.get_texto(306541, null);
										/* 	Esta etiqueta não corresponde a este processo!
											Favor verificar.	*/
			elsif (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
				ds_consistencia_w	:= wheb_mensagem_pck.get_texto(306542, null); -- Este horário esta suspenso na prescrição!
			end if;
		else
			ds_consistencia_w	:= wheb_mensagem_pck.get_texto(306556, null);
									/*	Etiqueta inválida! Este número não existe!
										Favor verificar.	*/
		end if;
		end;
	end if;

end if;

ds_consistencia_p := ds_consistencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_etiqueta_processo (nr_seq_processo_p bigint, nr_etiqueta_p text, ds_consistencia_p INOUT text) FROM PUBLIC;

