CREATE OR REPLACE PROCEDURE consistir_etiqueta_proc_hm (nr_seq_processo_p bigint, nr_etiqueta_p bigint, ds_consistencia_p INOUT text) AS $body$
DECLARE


ds_consistencia_w	varchar(240) := '';
ie_etiq_valida_w	varchar(1);
ie_etiq_processo_w	varchar(1);
nr_seq_frac_w		bigint;
nr_seq_digito_w		smallint;


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (nr_etiqueta_p IS NOT NULL AND nr_etiqueta_p::text <> '') then


	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_etiq_valida_w
	from	adep_processo
	where	nr_prescricao || nr_seq_procedimento || nr_etapa = nr_etiqueta_p;

	if (ie_etiq_valida_w = 'S') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_etiq_processo_w
		from	adep_processo
		where	nr_prescricao || nr_seq_procedimento || nr_etapa	= nr_etiqueta_p
		and	nr_sequencia						= nr_seq_processo_p;

		if (ie_etiq_processo_w = 'N') then

			ds_consistencia_w	:= wheb_mensagem_pck.get_texto(306541, null);
									/* 	Esta etiqueta não corresponde a este processo!
										Favor verificar.	*/
		end if;

	else
		ds_consistencia_w	:= wheb_mensagem_pck.get_texto(306556, null);
								/*	Etiqueta inválida! Este número não existe!
									Favor verificar.	*/
	end if;
end if;

ds_consistencia_p := ds_consistencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
