CREATE OR REPLACE PROCEDURE consistir_exigencia_cid_atb ( cd_material_p bigint, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_sequencia_p bigint, dt_prescricao_p timestamp, ie_agrupador_p bigint, nr_dia_util_p bigint, ie_novo_ciclo_p text, ie_exige_cid_atb_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

 
ie_exige_cid_atb_w		varchar(1);
ie_controle_w			smallint;
ie_controle_medico_w		bigint;
qt_dias_util_medic_w		bigint;
qt_dias_liberado_w		integer;
qt_dias_lib_atend_w		bigint;
ie_dias_util_medic_w		varchar(1);
ie_cid_medic_preenchido_w	varchar(1);


BEGIN 
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	select	max(obter_controle_medic(cd_material,wheb_usuario_pck.get_cd_estabelecimento,ie_controle_medico,null,null,null)), 
			max(ie_dias_util_medic) 
	into STRICT		ie_controle_w, 
			ie_dias_util_medic_w 
	from		material 
	where	cd_material	= cd_material_p;
	 
	SELECT * FROM consiste_util_medic( 
		nr_prescricao_p, nr_atendimento_p, dt_prescricao_p, cd_material_p, qt_dias_util_medic_w, qt_dias_liberado_w, ie_controle_medico_w, qt_dias_lib_atend_w) INTO STRICT qt_dias_util_medic_w, qt_dias_liberado_w, ie_controle_medico_w, qt_dias_lib_atend_w;
 
	ie_cid_medic_preenchido_w := obter_se_cid_medic_preenchido(nr_prescricao_p,nr_sequencia_p);
		 
	if (ie_agrupador_p = 1) and (ie_controle_w > 0) and 
		((ie_novo_ciclo_p = 'S') or (ie_controle_medico_w = 9) or 
		((ie_dias_util_medic_w = 'S' AND nr_dia_util_p	= 1) or 
		(ie_dias_util_medic_w	= 'O' AND nr_dia_util_p = 0))) and (ie_cid_medic_preenchido_w = 'N') then 
		begin 
		ie_exige_cid_atb_w := 'S';
		end;
	else 
		ie_exige_cid_atb_w := 'N';
	end if;	
	end;
end if;
ie_exige_cid_atb_p := ie_exige_cid_atb_w;
commit;
end;
$body$
