CREATE OR REPLACE PROCEDURE consistir_geracao_prescr_gt ( nr_transcricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_prescricao_gerada_w	varchar(1);


BEGIN
if (nr_transcricao_p IS NOT NULL AND nr_transcricao_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_prescricao_gerada_w
	from	prescr_medica
	where	nr_seq_transcricao	=	nr_transcricao_p;

	if (ie_prescricao_gerada_w = 'N') then
		update	transcricao_prescricao
		set	nm_usuario_transcricao	 = NULL,
			dt_transcricao		 = NULL
		where	nr_sequencia		= nr_transcricao_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
