CREATE OR REPLACE PROCEDURE consistir_gerar_prescr_rep ( cd_funcao_rep_p bigint, nr_prescricao_p bigint, nr_sequencia_p bigint, nr_cirurgia_p bigint, nr_seq_pepo_p bigint, cd_perfil_p bigint, nr_seq_consulta_oft_p bigint default 0, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE

 
ds_erro_w		varchar(255);
ds_proced_glosa_w	varchar(255);
ds_usuario_w		varchar(255);
ds_perfil_w		varchar(255);

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	if (cd_funcao_rep_p = 874) and (nr_sequencia_p > 0) then 
		begin 
		update	conclusao_recom_apae 
		set	nr_prescricao	= nr_prescricao_p, 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_seq_aval_pre	= nr_sequencia_p;
		end;
	end if;
	 
	if (nr_cirurgia_p > 0) or (nr_seq_pepo_p > 0) then 
		begin 
		update	prescr_medica 
		set	nr_cirurgia	= CASE WHEN nr_cirurgia_p=0 THEN null  ELSE nr_cirurgia_p END , 
			nr_seq_pepo	= CASE WHEN nr_seq_pepo_p=0 THEN null  ELSE nr_seq_pepo_p END , 
			ie_tipo_prescr_cirur	= 2, 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_prescricao	= nr_prescricao_p;
		end;
	end if;
	if (coalesce(nr_seq_consulta_oft_p,0) > 0 
		and coalesce(nr_prescricao_p,0) >0) then 
		CALL vincular_oftalmo_prescricao(nr_prescricao_p,nr_seq_consulta_oft_p,nm_usuario_p);
	end if;
	 
	consistir_prescricao( 
		nr_prescricao_p, 
		cd_perfil_p, 
		nm_usuario_p, 
		ds_erro_w, 
		ds_usuario_w, 
		ds_perfil_w, 
		ds_proced_glosa_w);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
