CREATE OR REPLACE PROCEDURE consistir_gerar_transferencia ( nr_sequencia_p bigint, nr_seq_tipo_p bigint, ie_exige_p text, ie_null_p text, nm_usuario_p text, dt_referencia_p timestamp default clock_timestamp()) AS $body$
DECLARE


ie_result_w	varchar(1);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	coalesce(ie_transferencia, 'N')
	into STRICT	ie_result_w
	from	pat_tipo_historico
	where	nr_sequencia = nr_sequencia_p;

	if (ie_result_w = 'S') then
		begin
		if (ie_exige_p IS NOT NULL AND ie_exige_p::text <> '') and (ie_null_p IS NOT NULL AND ie_null_p::text <> '') then
			begin
			CALL wheb_mensagem_pck.exibir_mensagem_abort(48913);
			end;
		end if;

		CALL pat_gerar_transf_docto(nr_sequencia_p, nm_usuario_p,clock_timestamp());

		end;
	end if;

	select	coalesce(ie_valor, 'N')
	into STRICT	ie_result_w
	from	pat_tipo_historico
	where	nr_sequencia = nr_sequencia_p;

	if (ie_result_w in ('B','V')) then
		begin
		pat_gerar_baixa_doc_bem(nr_sequencia_p, nm_usuario_p, dt_referencia_p);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
