SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_glicemia_estavel (nr_atendimento_p bigint, nr_seq_glicemia_p bigint, nr_cig_atend_p bigint, ie_estavel_p INOUT text) AS $body$
DECLARE


/*
Considerando as três últimas medições de Glicemia registradas para o atendimento,
caso todas estas estejam entre 90 e 120 a Glicemia é considerada estável.
*/
ie_estavel_w	varchar(1) := 'N';
qt_glicemia_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_glicemia_p IS NOT NULL AND nr_seq_glicemia_p::text <> '') and (nr_cig_atend_p IS NOT NULL AND nr_cig_atend_p::text <> '') then
	/* obter registros */

	select	count(*)
	into STRICT	qt_glicemia_w
	from	atendimento_cig
	where	nr_atendimento = nr_atendimento_p
	and	nr_seq_glicemia = nr_seq_glicemia_p
	and	nr_cig_atend = nr_cig_atend_p
	and	ie_status_cig not in ('T', 'V');

	/* validar registros */

	if (qt_glicemia_w > 2) then
		/* consistir estabilidade */

		select	CASE WHEN count(*)=3 THEN  'S'  ELSE 'N' END
		into STRICT	ie_estavel_w
		from (
			SELECT	qt_hgt
			from (
				SELECT	qt_hgt
				from	atendimento_cig
				where	nr_atendimento = nr_atendimento_p
				and	nr_seq_glicemia = nr_seq_glicemia_p
				and	nr_cig_atend = nr_cig_atend_p
				and	ie_status_cig <> 'T'
				order by
					nr_cig_atend desc, nr_cig desc
				) alias2 LIMIT 3) alias3
		where	qt_hgt between 90 and 120;
	end if;
end if;

ie_estavel_p := ie_estavel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
