CREATE OR REPLACE PROCEDURE consistir_guia_duplic_paciente ( cd_convenio_p bigint, nr_guia_p text, nr_atendimento_p bigint, ds_consistencia_p INOUT text) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
					

BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	coalesce(max('N'),'S') 
into STRICT	ds_consistencia_p 
from	atendimento_paciente b, 
	atend_categoria_convenio a 
where	a.nr_atendimento = b.nr_atendimento 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_w 
and	a.nr_doc_convenio = nr_guia_p 
and	a.cd_convenio = cd_convenio_p 
and	a.nr_atendimento <> nr_atendimento_p;
 
end;
