CREATE OR REPLACE PROCEDURE consistir_hemocompon_prescr ( nr_prescricao_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_sequencia_w	bigint;
ds_erro_w	varchar(2000);

C01 CURSOR FOR 
SELECT	nr_sequencia 
from	prescr_procedimento 
where	nr_prescricao = nr_prescricao_p 
and 	(nr_seq_solic_sangue IS NOT NULL AND nr_seq_solic_sangue::text <> '');

BEGIN
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_erro_w := consistir_prescr_procedimento(nr_prescricao_p, nr_sequencia_w, nm_usuario_p, cd_perfil_p, ds_erro_w);
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
