SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_horario_medico_aux ( nr_seq_agenda_p bigint, cd_medico_p text, nr_minuto_duracao_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


dt_inicial_w			timestamp;
dt_final_w			timestamp;
nr_seq_agenda_w			agenda_paciente.nr_sequencia%type;
nm_paciente_w			varchar(60);
ds_agenda_w			varchar(50);
dt_agenda_w			timestamp;
nm_medico_w			varchar(60);
cd_agenda_w			bigint;
cd_estabelecimento_w		integer;
ie_forma_cons_hor_medic_w	varchar(1);
cd_pessoa_fisica_w		varchar(10);
ie_somente_cirur_w		varchar(1);
cd_tipo_agenda_w		bigint;


BEGIN


SELECT	hr_inicio,
	hr_inicio + (coalesce(nr_minuto_duracao_p, nr_minuto_duracao) / 1440) - (1/86400),
	cd_agenda
INTO STRICT	dt_inicial_w,
	dt_final_w,
	cd_agenda_w
FROM	agenda_paciente
WHERE	nr_sequencia	= nr_seq_agenda_p;

SELECT	coalesce(MAX(nr_sequencia),0)
INTO STRICT	nr_seq_agenda_w
FROM	agenda_paciente
WHERE	((hr_inicio BETWEEN dt_inicial_w AND dt_final_w) OR
	(hr_inicio + (nr_minuto_duracao / 1440) - (1/86400) BETWEEN dt_inicial_w AND dt_final_w) OR
	((hr_inicio < dt_inicial_w) AND (hr_inicio + (nr_minuto_duracao / 1440) - (1/86400) > dt_final_w)))
and 	cd_auxiliar = cd_medico_p
AND	nr_sequencia	<> nr_seq_agenda_p
AND	ie_status_agenda NOT IN ('C','L','B','II');

IF (nr_seq_agenda_w > 0) THEN
	SELECT	SUBSTR(obter_nome_pf(cd_pessoa_fisica),1,60),
		hr_inicio,
		SUBSTR(obter_nome_agenda(cd_agenda),1,50),
		SUBSTR(obter_nome_pf(cd_auxiliar),1,60),
		obter_tipo_agenda(cd_agenda)
	INTO STRICT	nm_paciente_w,
		dt_agenda_w,
		ds_agenda_w,
		nm_medico_w,
		cd_tipo_agenda_w
	FROM	agenda_paciente
	WHERE	nr_sequencia	= nr_seq_agenda_w;
	
	IF (nm_medico_w IS NOT NULL AND nm_medico_w::text <> '') THEN
		ds_erro_p :=	WHEB_MENSAGEM_PCK.get_texto(278306,'NM_MEDICO='||nm_medico_w||';NM_PACIENTE='||nm_paciente_w||';DT_AGENDA='||TO_CHAR(dt_agenda_w,'dd/mm/yyyy hh24:mi')||';DS_AGENDA='||ds_agenda_w);
	END IF;
END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
