CREATE OR REPLACE PROCEDURE consistir_horario_processo (nr_seq_horario_p bigint) AS $body$
DECLARE


ie_permite_alterar_w	char(1) := 'S';

BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	select	coalesce(max('N'),'S')
	into STRICT	ie_permite_alterar_w
	from	adep_processo b,
			prescr_mat_hor a where		b.nr_sequencia	= a.nr_seq_processo
	and		a.nr_sequencia 	= nr_seq_horario_p
	and		Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S' LIMIT 1;

	if (ie_permite_alterar_w = 'N') then
		-- Este horário já está vinculado a um processo.
		--  Somente é possível alterá-lo através do mesmo! Favor verificar.#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261468);
	end if;
end if;

end;
$body$
