SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_impedimento_pf ( cd_pessoa_fisica_p text, ie_impedimento_p text, nm_usuario_p text) AS $body$
DECLARE


ie_impedimento_w	varchar(1);
nm_pessoa_fisica_w	varchar(60);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(substr(obter_nome_pf(cd_pessoa_fisica),1,60))
	into STRICT	nm_pessoa_fisica_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	select	max(obter_se_impedimento(cd_pessoa_fisica_p,'TOTAL'))
	into STRICT	ie_impedimento_w
	;

	if (ie_impedimento_w = 'S') then
		--nm_pessoa_fisica_w||' não pode estar vinculado a nada no sistema. Favor verificar impedimentos.#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261424, 'PESSOA='|| nm_pessoa_fisica_w);
	end if;

	select	max(obter_se_impedimento(cd_pessoa_fisica_p,ie_impedimento_p))
	into STRICT	ie_impedimento_w
	;

	if (ie_impedimento_w = 'S') then
		begin
		if (ie_impedimento_p = 'REP') then
			--nm_pessoa_fisica_w||' não pode ser médico nem prescritor. Favor verificar impedimentos.#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261425, 'PESSOA='|| nm_pessoa_fisica_w);
		elsif (ie_impedimento_p = 'PEP') then
			--nm_pessoa_fisica_w||' não pode acessar o PEP. Favor verificar impedimentos.#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261427, 'PESSOA='|| nm_pessoa_fisica_w);
		elsif (ie_impedimento_p = 'AS') then
			--nm_pessoa_fisica_w||' não pode acessar o sistema. Favor verificar impedimentos.#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261428, 'PESSOA='|| nm_pessoa_fisica_w);
		elsif (ie_impedimento_p = 'EUP') then
			--nm_pessoa_fisica_w||' não pode ser o médico responsável pelo paciente. Favor verificar impedimentos.#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261429, 'PESSOA='|| nm_pessoa_fisica_w);
		elsif (ie_impedimento_p = 'CIR') then
			--nm_pessoa_fisica_w||' não pode ser o médico cirurgião. Favor verificar impedimentos.#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261430, 'PESSOA='|| nm_pessoa_fisica_w);
		elsif (ie_impedimento_p = 'ANES') then
			--nm_pessoa_fisica_w||' não pode ser o anestesista. Favor verificar impedimentos.#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261431, 'PESSOA='|| nm_pessoa_fisica_w);
		end if;
		end;
	end if;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
