CREATE OR REPLACE PROCEDURE consistir_inicio_cirurgia ( nr_atendimento_p bigint, nr_cirurgia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


ie_consistir_passagem_w	varchar(1);
ie_possui_passagem_w	varchar(1);


BEGIN

ie_consistir_passagem_w := Obter_Param_Usuario(872, 61, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consistir_passagem_w);

if (ie_consistir_passagem_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_passagem_w
	from	atend_paciente_unidade
	where	obter_classif_setor(cd_setor_atendimento) = 2
	and		coalesce(dt_saida_unidade::text, '') = ''
	and		nr_atendimento	= nr_atendimento_p;

	if (ie_possui_passagem_w	= 'S') then
		ds_consistencia_p	:= wheb_mensagem_pck.get_texto(300259);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
