CREATE OR REPLACE PROCEDURE consistir_instal_dispositivo ( nr_atendimento_p bigint, nr_seq_dispositivo_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


/*

AL - Alerta
LI - Limite mÃ¡ximo

*/
qt_registro_w		integer;
qt_max_disp_ativo_w	smallint;
qt_max_alerta_w		smallint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	atend_pac_dispositivo
where	nr_atendimento		=	nr_atendimento_p
and	nr_seq_dispositivo	=	nr_seq_dispositivo_p
and 	obter_se_exibe_graf_disp(nr_sequencia) = 'S'
and	coalesce(dt_retirada::text, '') = '';

select	coalesce(max(qt_max_disp_ativo),999),
	coalesce(max(qt_max_alerta),999)
into STRICT	qt_max_disp_ativo_w,
	qt_max_alerta_w
from	dispositivo
where	nr_sequencia	=	nr_seq_dispositivo_p;

if (qt_registro_w >= qt_max_disp_ativo_w) then
	ds_retorno_p := 'LI';
elsif (qt_registro_w >= qt_max_alerta_w) then
	ds_retorno_p := 'AL';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
