CREATE OR REPLACE PROCEDURE consistir_item_transf_estoque ( cd_material_p bigint, nr_ordem_p bigint, cd_local_estoque_p bigint, cd_estabelecimento_p bigint, VarConsisteMaterialLocal_p text, VarConsisteMaterialLib_p text, ds_erro INOUT text) AS $body$
DECLARE


ie_material_estoque_w		varchar(1);
ie_req_mat_estoque_w		varchar(1);
ie_local_valido_w			varchar(1);
ds_erro_w			varchar(255);
ds_material_w			varchar(255);


BEGIN

select	distinct
	substr(obter_se_material_estoque(cd_estabelecimento_p, 0, cd_material_p),1,1),
	ds_material
into STRICT	ie_material_estoque_w,
	ds_material_w
from	material
where	cd_material = cd_material_p;


select	substr(ie_req_mat_estoque,1,1)
into STRICT	ie_req_mat_estoque_w
from	local_estoque
where	cd_local_estoque = cd_local_estoque_p;

if (VarConsisteMaterialLocal_p = 'S') then
	begin
	ie_local_valido_w := OBTER_LOCAL_VALIDO(cd_estabelecimento_p, cd_local_estoque_p, cd_material_p, '2', ie_local_valido_w);
	if (ie_local_valido_w <> 'S') then
		ds_erro_w := substr(ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(279707,'DS_MATERIAL='||cd_material_p),1,255);
	end if;
	if (cd_local_estoque_p IS NOT NULL AND cd_local_estoque_p::text <> '') then
		ie_local_valido_w := OBTER_LOCAL_VALIDO(cd_estabelecimento_p, cd_local_estoque_p, cd_material_p, '2', ie_local_valido_w);
		if (ie_local_valido_w <> 'S') then
			ds_erro_w := substr(ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(279708,'DS_MATERIAL='||cd_material_p),1,255);
		end if;
	end if;
	end;
end if;


if (ie_req_mat_estoque_w = 'S') and (ie_material_estoque_w <> 'S') then
	ds_erro_w := substr(ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(279709,'CD_MATERIAL='||cd_material_p),1,255);
end if;

ds_erro := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
