SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_itens_compostos ( nr_prescricao_p bigint, ie_consiste_Superior_p text, ie_consiste_diluente_p text) AS $body$
DECLARE


nr_agrupamento_w	double precision;
cont_w			bigint;
nr_seq_material_w	bigint;

c01 CURSOR FOR
SELECT	distinct
	nr_agrupamento
from	prescr_material a
where	a.nr_prescricao		= nr_prescricao_p
and	coalesce(nr_sequencia_solucao::text, '') = ''
and	coalesce(nr_sequencia_dieta::text, '') = ''
and	coalesce(nr_sequencia_diluicao::text, '') = ''
and     coalesce(a.dt_suspensao::text, '') = ''
and     coalesce(ie_administrar,'S')	= 'S'
and	ie_agrupador		in (1,5)
and	obter_tipo_material(a.cd_material, 'C') in (2,3);

c02 CURSOR FOR
SELECT	nr_sequencia
from	prescr_material a
where	nr_prescricao		= nr_prescricao_p
and	nr_agrupamento		= nr_agrupamento_w
and	((coalesce(ie_item_superior::text, '') = '') or (ie_item_superior	= 'N'))
and	coalesce(nr_sequencia_solucao::text, '') = ''
and	coalesce(nr_sequencia_dieta::text, '') = ''
and	coalesce(nr_sequencia_diluicao::text, '') = ''
and     coalesce(a.dt_suspensao::text, '') = ''
and     coalesce(ie_administrar,'S')	= 'S'
and	ie_agrupador		in (1,5)
and	obter_tipo_material(a.cd_material, 'C') in (2,3);


BEGIN

open C01;
loop
fetch C01 into
	nr_agrupamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	count(*)
	into STRICT	cont_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_agrupamento		= nr_agrupamento_w
	and	coalesce(nr_sequencia_solucao::text, '') = ''
	and	coalesce(nr_sequencia_dieta::text, '') = ''
	and	coalesce(nr_sequencia_diluicao::text, '') = ''
	and     coalesce(dt_suspensao::text, '') = ''
	and     coalesce(ie_administrar,'S')	= 'S'
	and	ie_agrupador		in (1,5)
	and	obter_tipo_material(cd_material, 'C') in (2,3);

	if (cont_w	> 1) then

		if (ie_consiste_Superior_p	= 'S') then
			if (cont_w	> 1) then
				select	count(*)
				into STRICT	cont_w
				from	prescr_material
				where	nr_prescricao		= nr_prescricao_p
				and	nr_agrupamento		= nr_agrupamento_w
				and	ie_item_superior	= 'S'
				and	coalesce(nr_sequencia_solucao::text, '') = ''
				and	coalesce(nr_sequencia_dieta::text, '') = ''
				and	coalesce(nr_sequencia_diluicao::text, '') = ''
				and     coalesce(dt_suspensao::text, '') = ''
				and     coalesce(ie_administrar,'S')	= 'S'
				and	ie_agrupador		in (1,5)
				and	obter_tipo_material(cd_material, 'C') in (2,3);

				if (cont_w	= 0) then
					--Exitem itens compostos sem item superior definido! Agrup.: '|| nr_agrupamento_w
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(261379,'AGRUPAMENTO='||nr_agrupamento_w);
				end if;
			end if;
		end if;

		if (ie_consiste_diluente_p	= 'S') then

			open c02;
			loop
			fetch c02 into
				nr_seq_material_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */
				select	count(*)
				into STRICT	cont_w
				from	prescr_material
				where	nr_prescricao		= nr_prescricao_p
				and	nr_sequencia_diluicao	= nr_seq_material_w
				and	((coalesce(ie_item_superior::text, '') = '') or (ie_item_superior	= 'N'))
				and	ie_agrupador		= 3;

				if (cont_w	> 0) then
					--Apenas os itens superiores devem ter diluição, favor verificar. Agrup.: '|| nr_agrupamento_w
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(261380,'AGRUPAMENTO='||nr_agrupamento_w);
				end if;
			end loop;
			close c02;
		end if;
	end if;

end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_itens_compostos ( nr_prescricao_p bigint, ie_consiste_Superior_p text, ie_consiste_diluente_p text) FROM PUBLIC;

