CREATE OR REPLACE PROCEDURE consistir_itens_nao_padrao ( nr_prescricao_p bigint, cd_estabelecimento_p bigint, ds_mensagem_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_material_w		varchar(255);

c01 CURSOR FOR
SELECT	substr(obter_desc_material(b.cd_material),1,80)
from	prescr_medica a,
	prescr_material b
where	a.nr_prescricao	= b.nr_prescricao
and	b.ie_agrupador	= 1
and	a.nr_prescricao	= nr_prescricao_p
and	coalesce(substr(obter_se_material_padronizado(cd_estabelecimento_p,b.cd_material),1,1),'S') = 'N'
and	coalesce(b.ie_medicacao_paciente,'N')	 <> 'S'
and	coalesce(Obter_se_medic_lib_pac2(a.cd_pessoa_fisica, b.cd_material, a.dt_prescricao, cd_estabelecimento_p, nm_usuario_p),'N') = 'N';


BEGIN

open C01;
loop
fetch C01 into
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= ds_material_w;
	else
		ds_retorno_w	:= substr(ds_material_w || ';  ' || ds_retorno_w,1,255);
	end if;
end loop;
close C01;

ds_mensagem_p	:= ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
