CREATE OR REPLACE PROCEDURE consistir_justif_interacao ( ds_justificativa_p text, nr_prescricao_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ie_lib_interacao_w		varchar(5);
ie_obriga_justif_w		varchar(5);
ie_lib_prescr_interacao_w	varchar(5);
ie_obriga_justif_interacao_w	varchar(5);


BEGIN
ie_obriga_justif_w := Obter_Param_Usuario(924, 215, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_obriga_justif_w);
ie_lib_interacao_w := Obter_Param_Usuario(924, 216, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_lib_interacao_w);

ie_lib_prescr_interacao_w	:= Obter_se_lib_prescr_interacao(nr_prescricao_p,ie_lib_interacao_w);
ie_obriga_justif_interacao_w	:= Obter_se_obriga_just_interacao(nr_prescricao_p,ie_obriga_justif_w);

if (ie_lib_interacao_w IS NOT NULL AND ie_lib_interacao_w::text <> '') and (ie_lib_prescr_interacao_w	= 'N') then

	ds_retorno_p	:= obter_desc_expressao(511235);

elsif (ie_obriga_justif_w IS NOT NULL AND ie_obriga_justif_w::text <> '') and (coalesce(ds_justificativa_p::text, '') = '') and (ie_obriga_justif_interacao_w	= 'S') then

	ds_retorno_p	:= obter_desc_expressao(511236);

elsif (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then

	update	prescr_medica
	set	ds_justificativa	= substr(ds_justificativa_p,1,2000)
	where	nr_prescricao		= nr_prescricao_p;

	commit;

end if;

end;
