CREATE OR REPLACE PROCEDURE consistir_leitura_barras (nr_seq_processo_p bigint, cd_material_p bigint, vl_retorno_p INOUT text) AS $body$
DECLARE


vl_retorno_w	varchar(1);


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	vl_retorno_w
	from	prescr_mat_hor b,
		adep_processo_item a
	where	b.nr_sequencia 	= a.nr_seq_horario
	and	b.ie_agrupador 	= 1
	and	a.ie_barras		= 'N'
	and	a.ie_suspenso		= 'N'
	and	b.cd_material		= cd_material_p
	and	a.nr_seq_processo	= nr_seq_processo_p
	and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S';

end if;

vl_retorno_p := vl_retorno_w;

end;
