SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_liberacao_prescr ( nr_prescricao_p bigint, ds_var_liberar_inc_p text, ds_var_liberacao_parcial_p text, ds_inicio_analise_prescricao_p text, ds_var_nm_usuario_analise_p text, nm_usuario_p text, ds_var_consis_usu_prescr_p text, cd_prescritor_p text, cd_pessoa_fisica_p text, nr_prescricao_original_p bigint, ds_var_ie_lib_disp_undef_p text, ds_liberar_sem_item_princ_p text, ds_var_lib_com_diluicao_p text, ie_consiste_superior_p text, ie_consiste_diluente_p text, ds_consiste_hor_conflito_p text, qt_prescr_confilto_p INOUT bigint) AS $body$
DECLARE


qt_prescricao_w		bigint;
qt_prescr_confilto_w	bigint 	:= 0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '')then
	begin

	select  count(*)
	into STRICT	qt_prescricao_w
	from    prescr_material a
	where   coalesce(a.ie_suspenso,'N') <> 'S'
	and     a.nr_prescricao = nr_prescricao_p
	and     ((a.nr_seq_inconsistencia IS NOT NULL AND a.nr_seq_inconsistencia::text <> '') or (exists (SELECT   1
								   from     prescr_material_incon_farm y
								   where    a.nr_prescricao 		= y.nr_prescricao
								   and	    y.nr_seq_material		= a.nr_sequencia
								   and	    coalesce(y.ie_situacao,'A') 	= 'A')));

	if (qt_prescricao_w = 0) then
		select  count(*)
		into STRICT	qt_prescricao_w
		from    prescr_solucao a
		where   coalesce(a.ie_suspenso,'N') <> 'S'
		and     a.nr_prescricao = nr_prescricao_p
		and     ((a.nr_seq_inconsistencia IS NOT NULL AND a.nr_seq_inconsistencia::text <> '') or (exists (SELECT   1
									   from     prescr_material_incon_farm y
									   where    a.nr_prescricao 		= y.nr_prescricao
									   and	    y.nr_seq_solucao		= a.nr_seq_solucao
									   and	    coalesce(y.ie_situacao,'A') 	= 'A')));
	end if;

	if (qt_prescricao_w = 0) then
		select  count(a.nr_sequencia)
		into STRICT	qt_prescricao_w
		from    prescr_gasoterapia a
		where   coalesce(a.ie_suspenso,'N') <> 'S'
		and     a.nr_prescricao = nr_prescricao_p
		and     (a.nr_seq_inconsistencia IS NOT NULL AND a.nr_seq_inconsistencia::text <> '');
	end if;

	if (ds_var_liberar_inc_p <> 'S') and (ds_var_liberacao_parcial_p <> 'S') and (qt_prescricao_w > 0) then
		-- Não é possível liberar a prescrição com inconsistências!
		CALL Wheb_mensagem_pck.exibir_mensagem_abort( 40128 );
	end if;

	if (ds_inicio_analise_prescricao_p = 'S') and (coalesce(ds_var_nm_usuario_analise_p::text, '') = '') then
		-- Para liberar esta prescrição deve ser iniciada a análise.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort( 40129 );
	end if;

	if (ds_inicio_analise_prescricao_p = 'S') and (ds_var_nm_usuario_analise_p <> nm_usuario_p) then
		-- Esta prescrição possui análise iniciada pelo usuário #@MENSAGEM#@. Somente este usuário poderá liberar esta prescrição.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort( 40170, 'MENSAGEM='||ds_var_nm_usuario_analise_p );
	end if;

	if (ds_var_consis_usu_prescr_p <> 'S') and (cd_prescritor_p = cd_pessoa_fisica_p) and
		((coalesce(nr_prescricao_original_p::text, '') = '') or (nr_prescricao_original_p = 0)) then
		-- O prescritor não pode liberar a prescrição como Farmacêutico
		CALL Wheb_mensagem_pck.exibir_mensagem_abort( 40172 );
	end if;

	if (ds_var_ie_lib_disp_undef_p <> 'S') then
		begin

		CALL consistir_dispensacao_farmacia(nr_prescricao_p);

		end;
	end if;

	if (ds_liberar_sem_item_princ_p <> 'S') and (ds_var_lib_com_diluicao_p <> 'S') then
		begin

		CALL consistir_itens_compostos(nr_prescricao_p, ie_consiste_superior_p, ie_consiste_diluente_p);

		end;
	end if;

	if (ds_consiste_hor_conflito_p = 'S')then
		begin

		CALL consistir_horarios_prescr(nr_prescricao_p);

		select 	count(*)
		into STRICT	qt_prescr_confilto_w
		from 	prescr_mat_hor_conflito
		where 	nr_prescricao = nr_prescricao_p;

		end;
	end if;

	end;
end if;

qt_prescr_confilto_p	:= qt_prescr_confilto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_liberacao_prescr ( nr_prescricao_p bigint, ds_var_liberar_inc_p text, ds_var_liberacao_parcial_p text, ds_inicio_analise_prescricao_p text, ds_var_nm_usuario_analise_p text, nm_usuario_p text, ds_var_consis_usu_prescr_p text, cd_prescritor_p text, cd_pessoa_fisica_p text, nr_prescricao_original_p bigint, ds_var_ie_lib_disp_undef_p text, ds_liberar_sem_item_princ_p text, ds_var_lib_com_diluicao_p text, ie_consiste_superior_p text, ie_consiste_diluente_p text, ds_consiste_hor_conflito_p text, qt_prescr_confilto_p INOUT bigint) FROM PUBLIC;

